import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import QRCode from 'qrcode';
import { FormControl, InputLabel, MenuItem, Select, Typography } from '@mui/material';

const pixPIX_KEY_CPVL = process.env.PIX_KEY_CPVL;

interface PaymentProps {
  valor: number;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ valor }) => {
  const { userId } = useParams<{ userId: string }>();
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  const [paymentLink, setPaymentLink] = useState<string>(pix1_CPVL);

  useEffect(() => {
    const paymentUrl = `https://link-de-pagamento-do-itau.com?valor=${valor}&descricao=PagamentoExemplo`;

    // Gerando o QR Code quando o valor mudar
    QRCode.toDataURL(paymentUrl)
      .then((qrCode: React.SetStateAction<string>) => {
        setQrCodeImage(qrCode);
        setPaymentLink(paymentUrl);
      })
      .catch((err: undefined) => {
        console.error('Erro ao gerar o QR Code:', err);
      });
  }, [valor]); // Sempre que o valor mudar, o QR Code será atualizado

  return (
    <div>
      <Typography variant="body2">
        {userId}
      </Typography>
      <FormControl fullWidth>
        <InputLabel id="tipoPagamento">Tipo do pagamento:</InputLabel>
        <Select
          labelId="tipoPagamento"
          id="tipopagamento"
          // value={}
          label="Tipo do pagamento"
          // onChange={handleChange}
        >
          <MenuItem value={'anual'}>Anual</MenuItem>
          <MenuItem value={'mensal'}>Mensal</MenuItem>
          <MenuItem value={'semestral'}>Semestral</MenuItem>
          <MenuItem value={'trimestal'}>Trimestral</MenuItem>
        </Select>
      </FormControl>
      <Typography variant="body1" sx={{mt: 2}}>
        Escaneie o QR Code abaixo para efetuar o pagamento.
      </Typography>
      {qrCodeImage && <img src={qrCodeImage} alt="QR Code de Pagamento mensalidade CPVL" />}
      <br />
      <p>Ou copie o link abaixo:</p>
      <input
        type="text"
        value={paymentLink}
        readOnly
        style={{ width: '100%', padding: '8px', marginTop: '8px' }}
      />
    </div>
  );
};
