import {
  ArrowBack as ArrowBackIcon,
  Person as PersonIcon,
  Badge as BadgeIcon
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Link,
  Typography,
  Card,
  CardContent,
  Grid,
  IconButton,
  Chip,
  Divider,
  CircularProgress,
  Alert
} from '@mui/material';
import { blueGrey, blue } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import { PaymentMonthly } from '../../components/PaymentMonthly';
import { PilotInfo } from '../../components/PilotInfo';
import { PaymentQRCode } from '../../components/PaymentQRCode';

interface IPilotDetails {
  id?: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
}

interface PilotDetailsProps {
  pilot: IPilotDetails;
}

export const PilotDetails = ({ pilot: initialPilot }: PilotDetailsProps) => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();
  const [pilot, setPilot] = useState<IPilotDetails | null>(null);

  const {
    data: pilotData,
    error: pilotError,
    loading: pilotLoading
  } = useFetch<IPilotDetails>({
    url: getURI(`${API.pilots}/${userId}`)
  });

  useEffect(() => {
    if (pilotData) {
      setPilot(pilotData);
    }
  }, [pilotData]);

  const handleBackClick = () => {
    navigate('/pilots');
  };

  if (pilotLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="400px"
      >
        <CircularProgress />
      </Box>
    );
  }

  if (pilotError || !pilot) {
    return (
      <Box sx={{ padding: '15px' }}>
        <Alert severity="error">
          Erro ao carregar detalhes do piloto. Piloto não encontrado.
        </Alert>
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ padding: '15px' }}>
        <Box display="flex" alignItems="center" mb={2}>
          <IconButton onClick={handleBackClick} sx={{ mr: 1 }}>
            <ArrowBackIcon />
          </IconButton>

          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Link
              underline="hover"
              color="inherit"
              onClick={handleBackClick}
              sx={{ cursor: 'pointer' }}
            >
              Pilotos
            </Link>
            <Typography color="text.primary">
              {pilot.firstName} {pilot.lastName}
            </Typography>
          </Breadcrumbs>
        </Box>

        {/* Card com informações do piloto */}
        <Card elevation={3} sx={{ mb: 3 }}>
          <PilotInfo pilot={pilot} />
        </Card>

        <Card elevation={3} sx={{ mb: 3 }}>
          {userId && <PaymentMonthly />}
        </Card>

        <Card elevation={3} sx={{ mb: 3 }}>
          {userId && <PaymentQRCode totalMissing={0} />}
        </Card>
      </Box>
    </Box>
  );
};
