import React, { useEffect, useState } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  Checkbox,
  FormControlLabel,
  Alert,
  CircularProgress
} from '@mui/material';
import { Link, useNavigate } from 'react-router-dom';
import InputMask from 'react-input-mask';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import {
  isEmail,
  isNotEmpty,
  isEqualToOtherValue,
  hasMinLength
} from '../../util/validation';

interface IInsertBody {
  name: string;
  cpf: string;
  cellphone: string;
  email: string;
  password: string;
  confirmPassword: string;
  agreeStatute: boolean;
  agreeRI: boolean;
  agreeLGPD: boolean;
  username?: string;
}

export const Signup: React.FC = () => {
  const navigate = useNavigate();

  const initialState: IInsertBody = {
    name: '',
    cpf: '',
    cellphone: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeStatute: false,
    agreeRI: false,
    agreeLGPD: false
  };

  const [formState, setFormState] = useState<IInsertBody>(initialState);
  const [errors, setErrors] = useState<string[]>([]);
  const [formError, setFormError] = useState<string | undefined>();
  const [fieldErrors, setFieldErrors] = useState<Record<string, string>>({});
  const [successMessage, setSuccessMessage] = useState<string | undefined>();

  const {
    doFetch: doAddPilot,
    data,
    error,
    loading
  } = useFetch<any>({ method: 'POST' });

  const stripNonDigits = (value: string) => value.replace(/\D/g, '');

  // ‚úÖ redireciona ap√≥s sucesso
  useEffect(() => {
    if (data) {
      setSuccessMessage(
        'Cadastro realizado com sucesso. Voc√™ ser√° redirecionado para o login.'
      );
      setFormState(initialState);

      const t = setTimeout(() => {
        navigate('/login');
      }, 2000);

      return () => clearTimeout(t);
    }
  }, [data, navigate]);

  // ‚úÖ erro gen√©rico
  useEffect(() => {
    if (error) {
      setFormError(
        typeof error === 'string'
          ? error
          : error!.message
            ? error!.message
            : 'Erro ao criar usu√°rio'
      );
    } else {
      setFormError(undefined);
    }
  }, [error]);

  // ‚úÖ envio do formul√°rio
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setErrors([]);
    setFormError(undefined);
    setSuccessMessage(undefined);
    setFieldErrors({});

    const {
      name,
      cpf,
      cellphone,
      email,
      password,
      confirmPassword,
      agreeStatute,
      agreeRI,
      agreeLGPD
    } = formState;

    const errorMessages: string[] = [];

    if (!isNotEmpty(name) || !hasMinLength(name, 10)) {
      errorMessages.push('Nome completo deve ter no m√≠nimo 10 caracteres');
    }
    if (!isNotEmpty(cpf) || stripNonDigits(cpf).length !== 11) {
      errorMessages.push('CPF deve ter exatamente 11 d√≠gitos');
    }
    if (!isNotEmpty(cellphone) || stripNonDigits(cellphone).length < 11) {
      errorMessages.push('Telefone deve ter no m√≠nimo 11 d√≠gitos');
    }
    if (!isNotEmpty(email) || !isEmail(email)) {
      errorMessages.push('Digite um E-mail v√°lido');
    }
    if (!isNotEmpty(password) || !hasMinLength(password, 6)) {
      errorMessages.push('Senha deve ter no m√≠nimo 6 caracteres');
    }
    if (!isEqualToOtherValue(password, confirmPassword)) {
      errorMessages.push('Senha e Confirma√ß√£o da Senha devem coincidir');
    }

    // üîπ valida√ß√µes de aceite
    if (!agreeStatute || !agreeRI || !agreeLGPD) {
      const newFieldErrors: Record<string, string> = {};
      if (!agreeStatute)
        newFieldErrors.agreeStatute =
          'Confirme o entendimento do Estatuto CPVL';
      if (!agreeRI)
        newFieldErrors.agreeRI =
          'Confirme o entendimento do Regimento Interno CPVL';
      if (!agreeLGPD)
        newFieldErrors.agreeLGPD = 'Confirme o entendimento da LGPD';
      setFieldErrors(newFieldErrors);
    }

    if (errorMessages.length > 0) {
      setErrors(errorMessages);
      return;
    }

    // üîπ prepara payload
    const cleanedCpf = stripNonDigits(cpf);
    const cleanedCellphone = stripNonDigits(cellphone);
    const normalizedEmail = String(email).trim().toLowerCase();
    const username = normalizedEmail.includes('@')
      ? normalizedEmail.split('@')[0]
      : normalizedEmail;

    const payload: IInsertBody = {
      ...formState,
      cpf: cleanedCpf,
      cellphone: cleanedCellphone,
      email: normalizedEmail,
      username
    };

    try {
      const result = await doAddPilot({
        url: getURI(API.userAdd),
        body: payload,
        method: 'POST'
      });
      console.log('Resultado add pilot', result);
      setSuccessMessage('Piloto cadastrado com sucesso');
    } catch (err: any) {
      console.error('Erro ao adicionar piloto', err);
      setFieldErrors({});
      setFormError(undefined);

      const errorData = err?.response?.data;
      if (errorData) {
        const { field, message } = errorData;
        if (field) {
          setFieldErrors({ [field]: message });
        } else {
          setFormError(message || 'Erro ao cadastrar usu√°rio.');
        }
      } else if (err?.status === 401) {
        setFormError('N√£o autorizado. Fa√ßa login novamente.');
      } else {
        setFormError('Erro ao conectar com o servidor.');
      }
    }
  };

  // ‚úÖ limpar tudo
  const handleReset = () => {
    setFormState(initialState);
    setErrors([]);
    setFormError(undefined);
    setSuccessMessage(undefined);
    setFieldErrors({});
  };

  // ‚úÖ alterar campo
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormState((prevState) => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value
    }));

    // limpa erro do campo alterado
    setFieldErrors((prev) => {
      const copy = { ...prev };
      delete copy[name];
      return copy;
    });

    setFormError(undefined);
    setErrors([]);
  };

  return (
    <Box
      sx={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '81vh',
        backgroundColor: '#f4f6f8'
      }}
    >
      <Box
        sx={{
          width: '100%',
          maxWidth: 400,
          padding: 3,
          borderRadius: 2,
          backgroundColor: 'white',
          boxShadow: 3
        }}
      >
        <Typography variant="h4" align="center" mb={2}>
          Cadastre-se
        </Typography>
        <Typography variant="body2" align="center" mb={4}>
          Preencha os campos abaixo para criar sua conta.
        </Typography>

        {formError && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {formError}
          </Alert>
        )}

        {successMessage && (
          <Alert severity="success" sx={{ mb: 2 }}>
            {successMessage}
          </Alert>
        )}

        <form onSubmit={handleSubmit}>
          <TextField
            fullWidth
            label="Nome completo"
            name="name"
            value={formState.name}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            required
            error={!!fieldErrors.name}
            helperText={fieldErrors.name}
          />

          <InputMask
            mask="999.999.999-99"
            value={formState.cpf}
            onChange={handleChange}
          >
            {(inputProps: any) => (
              <TextField
                {...inputProps}
                fullWidth
                label="CPF"
                name="cpf"
                variant="outlined"
                margin="normal"
                required
                error={!!fieldErrors.cpf}
                helperText={fieldErrors.cpf}
              />
            )}
          </InputMask>

          <InputMask
            mask="(99) 99999-9999"
            value={formState.cellphone}
            onChange={handleChange}
          >
            {(inputProps: any) => (
              <TextField
                {...inputProps}
                fullWidth
                label="Telefone"
                name="cellphone"
                variant="outlined"
                margin="normal"
                required
                error={!!fieldErrors.cellphone}
                helperText={fieldErrors.cellphone}
              />
            )}
          </InputMask>

          <TextField
            fullWidth
            label="E-mail"
            name="email"
            type="email"
            value={formState.email}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            required
            error={!!fieldErrors.email}
            helperText={fieldErrors.email}
          />

          <TextField
            fullWidth
            label="Senha"
            name="password"
            type="password"
            value={formState.password}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            required
            error={!!fieldErrors.password}
            helperText={fieldErrors.password}
          />

          <TextField
            fullWidth
            label="Confirme a senha"
            name="confirmPassword"
            type="password"
            value={formState.confirmPassword}
            onChange={handleChange}
            variant="outlined"
            margin="normal"
            required
            error={!!fieldErrors.confirmPassword}
            helperText={fieldErrors.confirmPassword}
          />

          {/* üîπ Estatuto */}
          <FormControlLabel
            control={
              <Checkbox
                name="agreeStatute"
                checked={formState.agreeStatute}
                onChange={handleChange}
                color={fieldErrors.agreeStatute ? 'error' : 'primary'}
              />
            }
            label={
              <Box component="span">
                Li e concordo com o{' '}
                <Link
                  to="/docs/EstatutoCPVL_2023.pdf"
                  download
                  target="_blank"
                >
                  <Typography
                    component="span"
                    lineHeight={2}
                    fontSize="18px"
                    fontFamily="Flamenco, system-ui"
                    fontWeight="400"
                  >
                    Estatuto CPVL
                  </Typography>
                </Link>
              </Box>
            }
          />
          {fieldErrors.agreeStatute && (
            <Typography color="error" variant="caption" sx={{ ml: 4 }}>
              {fieldErrors.agreeStatute}
            </Typography>
          )}

          {/* üîπ Regimento Interno */}
          <FormControlLabel
            control={
              <Checkbox
                name="agreeRI"
                checked={formState.agreeRI}
                onChange={handleChange}
                color={fieldErrors.agreeRI ? 'error' : 'primary'}
              />
            }
            label={
              <Box component="span">
                Li e concordo com o{' '}
                <Link
                  to="/docs/RegimentoInternoCPVL_2024.pdf"
                  download
                  target="_blank"
                >
                  <Typography
                    component="span"
                    lineHeight={2}
                    fontSize="18px"
                    fontFamily="Flamenco, system-ui"
                    fontWeight="400"
                  >
                    Regimento Interno CPVL
                  </Typography>
                </Link>
              </Box>
            }
          />
          {fieldErrors.agreeRI && (
            <Typography color="error" variant="caption" sx={{ ml: 4 }}>
              {fieldErrors.agreeRI}
            </Typography>
          )}

          {/* üîπ LGPD */}
          <FormControlLabel
            control={
              <Checkbox
                name="agreeLGPD"
                checked={formState.agreeLGPD}
                onChange={handleChange}
                color={fieldErrors.agreeLGPD ? 'error' : 'primary'}
              />
            }
            label={
              <Box component="span">
                Li e concordo com a{' '}
                <Link to="/docs/lgpd_cpvl.pdf" download target="_blank">
                  <Typography
                    component="span"
                    lineHeight={2}
                    fontSize="18px"
                    fontFamily="Flamenco, system-ui"
                    fontWeight="400"
                  >
                    LGPD
                  </Typography>
                </Link>
              </Box>
            }
          />
          {fieldErrors.agreeLGPD && (
            <Typography color="error" variant="caption" sx={{ ml: 4 }}>
              {fieldErrors.agreeLGPD}
            </Typography>
          )}

          {errors.length > 0 && (
            <Box sx={{ marginTop: 2 }}>
              {errors.map((err, idx) => (
                <Alert key={idx} severity="error" sx={{ marginBottom: 1 }}>
                  {err}
                </Alert>
              ))}
            </Box>
          )}

          <Box sx={{ marginTop: 2 }}>
            <Button
              variant="outlined"
              color="primary"
              type="button"
              fullWidth
              sx={{ marginBottom: 2 }}
              onClick={handleReset}
              disabled={loading}
            >
              Limpar
            </Button>

            <Button
              variant="contained"
              type="submit"
              fullWidth
              sx={{ bgcolor: '#1f5388' }}
              disabled={loading}
              startIcon={loading ? <CircularProgress size={18} /> : undefined}
            >
              {loading ? 'Cadastrando...' : 'Cadastrar'}
            </Button>
          </Box>
        </form>

        <Box sx={{ marginTop: 3 }}>
          <Typography variant="body2" align="center">
            J√° tem conta?{' '}
            <Link
              to="/login"
              style={{ textDecoration: 'underline', color: '#1f5388' }}
            >
              Fa√ßa login
            </Link>
          </Typography>
        </Box>
      </Box>
    </Box>
  );
};
