import { useState, useEffect } from 'react';
import useFetch from '../../hooks/useFetch';
import { useParams } from 'react-router-dom';

interface PaymentMonthly {
  id: number;
  userId: number;
  amount: number;
  ref_year: number;
  ref_month: number;
  type?: string;
  description?: string;
  date?: string;
}

interface Props {
  userId: number;
}

export const PaymentCheckbox: React.FC<Props> = () => {
  const { userId } = useParams<{ userId: string }>();
  const { doFetch, data, loading } = useFetch<PaymentMonthly>({
    method: 'POST'
  });

  const [checked, setChecked] = useState(false);

  // quando marcar o checkbox → insere na tabela
  const handleChange = async () => {
    const newChecked = !checked;
    setChecked(newChecked);

    if (newChecked) {
      await doFetch({
        url: `${process.env.REACT_APP_API_URL}/paymentMonthly`,
        body: {
          userId,
          amount: 50, // valor fixo ao marcar
          ref_year: new Date().getFullYear(),
          ref_month: new Date().getMonth() + 1,
          type: 'mensal',
          description: 'Pagamento manual via checkbox',
          date: new Date()
        },
        method: 'POST'
      });
    }
  };

  useEffect(() => {
    if (data && data.amount > 50) {
      setChecked(true);
    }
  }, [data]);

  return (
    <label>
      <input
        type="checkbox"
        checked={checked}
        onChange={handleChange}
        disabled={loading}
      />
      ''
    </label>
  );
};
