import {
  Payment as PaymentIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Schedule as ScheduleIcon,
  Block as BlockIcon,
} from '@mui/icons-material';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
} from '@mui/material';
import { blueGrey, blue, green, orange, red } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';

interface IPaymentMonthly {
  id?: number;
  userId: number;
  ref_year: number;
  ref_month: number;
  amount: number | string;
  status: string;
  type: string;
  Description: string;
  date: Date | string;
}

interface PaymentMonthlyProps {
  userId: string;
}

export const PaymentMonthly: React.FC<PaymentMonthlyProps> = ({ userId }) => {
  console.log('--> PaymentMonthly userId:', userId);
  const [payments, setPayments] = useState<IPaymentMonthly[]>([]);

  // Validate userId and construct API URL
  const isUserIdValid = userId && !isNaN(parseInt(userId));
  const apiUrl = isUserIdValid ? getURI(`${API.paymentMonthly}?userId=${userId}`) : undefined;
  console.log('--> API URL:', apiUrl); // Debug API URL

  // Fetch payments
  const {
    data: paymentsData,
    error: paymentsError,
    // loading: paymentsLoading,
  } = useFetch<IPaymentMonthly[]>({
    method: 'GET',
    url: apiUrl,
  });

  useEffect(() => {
    console.log('--> paymentsData:', paymentsData);
    console.log('--> paymentsError:', paymentsError);
    if (paymentsData) {
      setPayments(Array.isArray(paymentsData) ? paymentsData : []);
    } else if (paymentsError) {
      setPayments([]); // Clear payments on error to avoid stale data
    }
  }, [paymentsData, paymentsError]);

  // Format month
  const formatMonth = (month: number) => {
    const months = [
      'Janeiro',
      'Fevereiro',
      'Março',
      'Abril',
      'Maio',
      'Junho',
      'Julho',
      'Agosto',
      'Setembro',
      'Outubro',
      'Novembro',
      'Dezembro',
    ];
    return months[month - 1] || `Mês ${month}`;
  };

  // Parse amount
  const parseAmount = (amount: any): number => {
    if (!amount || amount === '') return 0;
    if (typeof amount === 'number') return amount;
    try {
      return parseFloat(amount.toString().replace(',', '.'));
    } catch (error) {
      console.error('Error parsing amount:', amount, error);
      return 0;
    }
  };

  // Get status color
  const getStatusColor = (status: string) => {
    switch (status.toLowerCase()) {
      case 'ativo':
        return green[500];
      case 'pendente':
        return orange[500];
      case 'inativo':
        return red[500];
      case 'trancado':
        return blueGrey[500];
      default:
        return blueGrey[300];
    }
  };

  // Get status icon
  const getStatusIcon = (status: string) => {
    switch (status.toLowerCase()) {
      case 'ativo':
        return <CheckCircleIcon sx={{ fontSize: 16, color: green[500] }} />;
      case 'pendente':
        return <ScheduleIcon sx={{ fontSize: 16, color: orange[500] }} />;
      case 'inativo':
        return <WarningIcon sx={{ fontSize: 16, color: red[500] }} />;
      case 'trancado':
        return <BlockIcon sx={{ fontSize: 16, color: blueGrey[500] }} />;
      default:
        return <WarningIcon sx={{ fontSize: 16, color: blueGrey[300] }} />;
    }
  };

  // Get status text
  const getStatusText = (status: string) => {
    return status && status.trim() !== '' ? status : 'Não definido';
  };

  // Calculate financial summary
  const getFinancialSummary = () => {
    const validPayments = payments.filter(
      (p) => p.status && p.status.trim() !== ''
    );

    const activePayments = validPayments.filter(
      (p) => p.status.toLowerCase() === 'ativo'
    ).length;
    const pendingPayments = validPayments.filter(
      (p) => p.status.toLowerCase() === 'pendente'
    ).length;
    const inactivePayments = validPayments.filter(
      (p) => p.status.toLowerCase() === 'inativo'
    ).length;
    const lockedPayments = validPayments.filter(
      (p) => p.status.toLowerCase() === 'trancado'
    ).length;
    const undefinedPayments = payments.filter(
      (p) => !p.status || p.status.trim() === ''
    ).length;
    const totalAmount = validPayments.reduce(
      (sum, p) => sum + parseAmount(p.amount),
      0
    );

    const monthlyStatus = validPayments
      .sort((a, b) => b.ref_year - a.ref_year || b.ref_month - a.ref_month)
      .map((payment) => ({
        year: payment.ref_year,
        month: payment.ref_month,
        monthName: formatMonth(payment.ref_month),
        status: payment.status,
        amount: parseAmount(payment.amount),
        type: payment.type || 'N/A',
        description: payment.Description || 'N/A',
        date: payment.date,
      }));

    return {
      total: payments.length,
      valid: validPayments.length,
      active: activePayments,
      pending: pendingPayments,
      inactive: inactivePayments,
      locked: lockedPayments,
      undefined: undefinedPayments,
      totalAmount,
      monthlyStatus,
    };
  };

  const financialSummary = getFinancialSummary();

  // Render invalid userId error
  if (!isUserIdValid) {
    console.error('Invalid userId:', userId);
    return (
      <Alert severity="error">
        ID de usuário inválido. Por favor, forneça um ID válido.
      </Alert>
    );
  }

  // // Render loading state
  // if (paymentsLoading) {
  //   return (
  //     <Box display="flex" justifyContent="center" p={3}>
  //       <CircularProgress />
  //     </Box>
  //   );
  // }

  // Render error state
  if (paymentsError) {
    let errorMessage = 'Erro ao carregar histórico de pagamentos.';
    if (paymentsError.message === 'Unauthorized') {
      errorMessage = 'Acesso não autorizado. Por favor, faça login novamente.';
    } else if (paymentsError.message === 'Forbidden resource') {
      errorMessage = 'Acesso proibido. Você não tem permissão para visualizar esses dados.';
    } else if (paymentsError.message) {
      errorMessage += ` Detalhes: ${paymentsError.message}`;
    }
    return (
      <Alert severity="error">
        {errorMessage} Verifique o ID do usuário ou tente novamente mais tarde.
      </Alert>
    );
  }

  // Render empty state
  if (payments.length === 0) {
    return (
      <Alert severity="info">
        Nenhum registro de pagamento encontrado para este piloto.
      </Alert>
    );
  }

  return (
    <>
      <Card elevation={3} sx={{ mb: 3 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom color="primary">
            Resumo Financeiro
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={green[50]} borderRadius={2}>
                <Typography variant="h4" color={green[600]}>
                  {financialSummary.active}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Ativos
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={orange[50]} borderRadius={2}>
                <Typography variant="h4" color={orange[600]}>
                  {financialSummary.pending}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Pendentes
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={red[50]} borderRadius={2}>
                <Typography variant="h4" color={red[600]}>
                  {financialSummary.inactive}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Inativos
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={blueGrey[50]} borderRadius={2}>
                <Typography variant="h4" color={blueGrey[600]}>
                  {financialSummary.locked}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Trancados
                </Typography>
              </Box>
            </Grid>
          </Grid>
          <Box mt={2}>
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <Box textAlign="center" p={2} bgcolor={blue[50]} borderRadius={2}>
                  <Typography variant="h5" color={blue[600]}>
                    R$ {financialSummary.totalAmount.toFixed(2)}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Valor Total
                  </Typography>
                </Box>
              </Grid>
              {financialSummary.undefined > 0 && (
                <Grid item xs={6}>
                  <Box
                    textAlign="center"
                    p={2}
                    bgcolor={blueGrey[50]}
                    borderRadius={2}
                    border={1}
                    borderColor={blueGrey[200]}
                  >
                    <Typography variant="h5" color={blueGrey[600]}>
                      {financialSummary.undefined}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Sem Status
                    </Typography>
                  </Box>
                </Grid>
              )}
            </Grid>
          </Box>
          <Box mt={3}>
            <Typography variant="subtitle1" gutterBottom color="primary">
              Status dos Últimos Meses
            </Typography>
            <Box display="flex" flexWrap="wrap" gap={1}>
              {financialSummary.monthlyStatus.slice(0, 12).map((month, index) => (
                <Chip
                  key={`${month.year}-${month.month}`}
                  label={`${month.monthName.substring(0, 3)}/${month.year}`}
                  size="small"
                  sx={{
                    backgroundColor:
                      month.status.toLowerCase() === 'ativo'
                        ? green[100]
                        : month.status.toLowerCase() === 'pendente'
                        ? orange[100]
                        : month.status.toLowerCase() === 'inativo'
                        ? red[100]
                        : month.status.toLowerCase() === 'trancado'
                        ? blueGrey[100]
                        : blueGrey[50],
                    color:
                      month.status.toLowerCase() === 'ativo'
                        ? green[800]
                        : month.status.toLowerCase() === 'pendente'
                        ? orange[800]
                        : month.status.toLowerCase() === 'inativo'
                        ? red[800]
                        : month.status.toLowerCase() === 'trancado'
                        ? blueGrey[800]
                        : blueGrey[600],
                    '&:hover': {
                      backgroundColor:
                        month.status.toLowerCase() === 'ativo'
                          ? green[200]
                          : month.status.toLowerCase() === 'pendente'
                          ? orange[200]
                          : month.status.toLowerCase() === 'inativo'
                          ? red[200]
                          : month.status.toLowerCase() === 'trancado'
                          ? blueGrey[200]
                          : blueGrey[100],
                    },
                  }}
                  title={`${month.monthName} ${month.year} - R$ ${month.amount.toFixed(2)} (${getStatusText(month.status)})`}
                />
              ))}
            </Box>
            {financialSummary.monthlyStatus.length > 12 && (
              <Typography
                variant="caption"
                color="textSecondary"
                sx={{ mt: 1, display: 'block' }}
              >
                Mostrando os 12 meses mais recentes de {financialSummary.valid} registros válidos
              </Typography>
            )}
            {financialSummary.monthlyStatus.length === 0 && (
              <Alert severity="info" sx={{ mt: 1 }}>
                Nenhum registro com status definido encontrado.
              </Alert>
            )}
          </Box>
        </CardContent>
      </Card>
      <Card elevation={3}>
        <CardContent>
          <Box display="flex" alignItems="center" mb={3}>
            <PaymentIcon sx={{ fontSize: 32, color: blue[500], mr: 2 }} />
            <Typography variant="h5" component="h2">
              Histórico de Pagamentos
            </Typography>
          </Box>
          <TableContainer component={Paper} elevation={0}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>
                    <strong>Período</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Tipo</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Valor</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Status</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Descrição</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Data</strong>
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {payments
                  .sort((a, b) => b.ref_year - a.ref_year || b.ref_month - a.ref_month)
                  .map((payment, index) => (
                    <TableRow key={`${payment.id || index}`} hover>
                      <TableCell>
                        <Typography variant="body2">
                          {formatMonth(payment.ref_month)} {payment.ref_year}
                        </Typography>
                      </TableCell>
                      <TableCell>
                        {payment.type ? (
                          <Chip label={payment.type} size="small" variant="outlined" />
                        ) : (
                          <Typography variant="body2" color="textSecondary">
                            -
                          </Typography>
                        )}
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2" fontWeight="bold">
                          R$ {parseAmount(payment.amount).toFixed(2)}
                        </Typography>
                      </TableCell>
                      <TableCell>
                        <Box display="flex" alignItems="center">
                          {getStatusIcon(payment.status)}
                          <Typography
                            variant="body2"
                            sx={{
                              ml: 1,
                              color: getStatusColor(payment.status),
                              textTransform: 'capitalize',
                            }}
                          >
                            {getStatusText(payment.status)}
                          </Typography>
                        </Box>
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2">
                          {payment.Description && payment.Description.trim() !== ''
                            ? payment.Description
                            : '-'}
                        </Typography>
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2">
                          {payment.date
                            ? new Date(payment.date).toLocaleDateString('pt-BR')
                            : '-'}
                        </Typography>
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>
          </TableContainer>
        </CardContent>
      </Card>
    </>
  );
};
