import {
  Add as AddIcon,
  EmailOutlined as Email,
  Phone,
  ArrowForward as ArrowForwardIcon,
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Divider,
  Link,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListSubheader,
  Typography,
  IconButton,
} from '@mui/material';
import { blueGrey } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import * as Styled from './StatusList.styled';

interface IPilot {
  id: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
}

interface ApiResponse {
  success: boolean;
  count: number;
  data: IPilot[];
}

export const StatusList = () => {
  const [pilots, setPilots] = useState<IPilot[]>([]);
  const { data, error, loading } = useFetch<ApiResponse>({
    url: getURI(API.status, { status: 'pendente' }), // Pass status as query param
  });

  useEffect(() => {
    if (data?.success && data.data?.length) {
      setPilots(data.data);
    }
  }, [data]);

  return (
    <Box>
      <Box sx={{ padding: '15px' }}>
        <Breadcrumbs aria-label="breadcrumb">
          <Link underline="hover" color="inherit" href="/">
            Dashboard
          </Link>
          <Typography color="text.primary">Pilotos</Typography>
        </Breadcrumbs>
      </Box>
      {loading && (
        <Box display="flex" justifyContent="center" alignItems="center" minHeight="400px">
          <Typography color="text.secondary">Carregando pilotos...</Typography>
        </Box>
      )}
      {error && (
        <Typography color="error" sx={{ padding: '15px' }}>
          Erro: {error}
        </Typography>
      )}
      {pilots?.length ? (
        <List
          subheader={
            <ListSubheader component="div" id="nested-list-subheader" color="primary">
              Total de {pilots.length} piloto(s) com status pendente
            </ListSubheader>
          }
        >
          {pilots.map((pilot) => (
            <Box key={pilot.id}>
              <ListItem
                disablePadding
                secondaryAction={
                  <IconButton edge="end" aria-label="ver detalhes">
                    <ArrowForwardIcon />
                  </IconButton>
                }
              >
                <ListItemButton>
                  <ListItemText
                    primary={`${pilot.firstName} ${pilot.lastName}`}
                    secondary={
                      <span style={{ display: 'flex', alignItems: 'center' }}>
                        <Phone sx={{ fontSize: '14px' }} />
                        <Typography sx={{ paddingLeft: '5px', paddingTop: '2px' }} variant="caption" color={blueGrey[500]}>
                          {pilot.cellphone}
                        </Typography>
                        <Email sx={{ fontSize: '15px', ml: 1 }} />
                        <Typography sx={{ paddingLeft: '5px' }} variant="caption" color={blueGrey[500]}>
                          {pilot.email}
                        </Typography>
                      </span>
                    }
                  />
                </ListItemButton>
              </ListItem>
              <Divider />
            </Box>
          ))}
        </List>
      ) : (
        !loading && <Typography sx={{ padding: '15px' }}>Nenhum piloto encontrado.</Typography>
      )}
      <Styled.AddButton color="primary" aria-label="add">
        <AddIcon />
      </Styled.AddButton>
    </Box>
  );
};
