import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  CircularProgress,
  Alert
} from '@mui/material';
import { useState, useEffect } from 'react';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import { useParams } from 'react-router-dom';

interface StatusPilotProps {
  userId: number;
  currentStatus: string;
  onStatusChange: (newStatus: string) => void;
}

export const StatusPilot = ({
  currentStatus,
  onStatusChange
}: StatusPilotProps) => {
  const { userId } = useParams<{ userId: string }>();
  const [status, setStatus] = useState(currentStatus);
  const [statusOptions, setStatusOptions] = useState<string[]>([]);
  const [errorMessage, setErrorMessage] = useState<string>('');

  // Hook para fazer a requisição
  const { doFetch, loading, error } = useFetch({
    method: 'PATCH'
  });

  useEffect(() => {
    const fetchStatusOptions = async () => {
      try {
        const response = await fetch(getURI(API.pilots) + '/statusOptions');
        if (response.ok) {
          const statusData = await response.json();
          setStatusOptions(statusData);
        } else {
          console.error('Erro ao carregar opções de status');
        }
      } catch (error) {
        console.error('Erro na requisição', error);
      }
    };

    fetchStatusOptions();
  }, []);

  const handleUpdateStatus = async () => {
    try {
      const response = await doFetch({
        url: getURI(API.updateStatusPilot),
        body: { userId, statusOptions },
        // url: getURI(
        //   `${API.updateStatusPilot}?userId=${userId}&status=${status}`
        // ),
        method: 'PATCH'
      });

      if (response) {
        alert('Status atualizado com sucesso!');
      }
      onStatusChange(status);
    } catch (error) {
      setErrorMessage('Erro ao atualizar status');
    }
  };

  return (
    <Box display="flex" alignItems="center" gap={2} mt={1}>
      <FormControl size="small">
        <InputLabel>Status</InputLabel>
        <Select
          value={status}
          label="Status"
          onChange={(e) => setStatus(e.target.value)}
          sx={{ borderRadius: 3, minWidth: 150 }}
        >
          {statusOptions.map((status) => (
            <MenuItem key={status} value={status}>
              {status.charAt(0).toUpperCase() + status.slice(1)}
            </MenuItem>
          ))}
        </Select>
      </FormControl>

      {loading ? (
        <CircularProgress />
      ) : (
        <Button
          variant="contained"
          color="primary"
          onClick={handleUpdateStatus}
          size="small"
          sx={{ borderRadius: 3 }}
        >
          Atualizar
        </Button>
      )}

      {errorMessage && <Alert severity="error">{errorMessage}</Alert>}
    </Box>
  );
};
