import React, { useState } from 'react';
import {
  TextField,
  Button,
  Box,
  Typography,
  Checkbox,
  FormControlLabel,
  Alert
} from '@mui/material';
import { Link } from 'react-router-dom';
import {
  isEmail,
  isNotEmpty,
  isEqualToOtherValue,
  hasMinLength
} from '../../util/validation';

export const Signup = () => {
  const initialFormState = {
    name: '',
    cpf: '',
    cellphone: '',
    email: '',
    password: '',
    confirmPassword: '',
    agreeStatute: false,
    agreeRegimentoInterno: false,
    agreeLGPD: false
  };

  const [formState, setFormState] = useState(initialFormState);
  const [formError, setFormError] = useState<string | undefined>();
  const [errors, setErrors] = useState<string[]>([]);

  // Handle change of input fields
  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value, type, checked } = e.target;
    setFormState((prevState) => ({
      ...prevState,
      [name]: type === 'checkbox' ? checked : value
    }));
    setFormError('');
    setErrors([]);
  };


  const isValidCPF = (cpf: string) => {
    const cpfValue = cpf.replace(/\.|\-/g, '');
    if (!hasMinLength(cpfValue, 11)) {
      return false;
    }
    let sum = 0;
    let weight = 10;
    for (let i = 0; i < 9; i++) {
      const digit = parseInt(cpfValue.charAt(i));
      sum += digit * weight;
      weight--;
    }
    const verifyingDigit = 11 - (sum % 11);
    if (verifyingDigit === 10 || verifyingDigit === 11) {
      return cpfValue.charAt(9) === '0';
    }
    return parseInt(cpfValue.charAt(9)) === verifyingDigit;
  };

  // Handle form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const {
      name,
      cpf,
      cellphone,
      email,
      password,
      confirmPassword,
      agreeStatute,
      agreeRegimentoInterno
    } = formState;
    const errorMessages: string[] = [];

    if (!isNotEmpty(name) || !hasMinLength(name, 10)) {
      errorMessages.push('Nome completo deve ter no mínimo 10 caracteres');
    }

    if (!isNotEmpty(cpf) || !isValidCPF(cpf)) {
      errorMessages.push('CPF deve ser válido');

      if (!isNotEmpty(cellphone) || !hasMinLength(cellphone, 11)) {
        errorMessages.push('Telefone deve ter no mínimo 11 caracteres');
      }

      if (!isNotEmpty(email) || !isEmail(email)) {
        errorMessages.push('Digite um E-mail válido');
      }

      if (!isNotEmpty(password) || !hasMinLength(password, 6)) {
        errorMessages.push('Senha deve ter no mínimo 6 caracteres');
      }

      if (!isEqualToOtherValue(password, confirmPassword)) {
        errorMessages.push('Senha e Confirmação da Senha devem coincidir');
      }

      if (!agreeStatute) {
        errorMessages.push('Confirme o entendimento do Estatuto CPVL');
      }

      if (!agreeRegimentoInterno) {
        errorMessages.push('Confirme o entendimento do Regimento Interno CPVL');
      }

      if (!agreeRegimentoInterno) {
        errorMessages.push('Confirme o entendimento da LGPD');
      }

      if (errorMessages.length > 0) {
        setErrors(errorMessages);
        return;
      }

      // Simulating form submission (you would send it to an API here)
      console.log('Form Submitted', formState);
    };

    // Reset form to initial state
    const handleReset = () => {
      setFormState(initialFormState);
      setErrors([]);
      setFormError('');
    };

    return (
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          minHeight: '81vh',
          backgroundColor: '#f4f6f8'
        }}
      >
        <Box
          sx={{
            width: '100%',
            maxWidth: 400,
            padding: 3,
            borderRadius: 2,
            backgroundColor: 'white',
            boxShadow: 3
          }}
        >
          <Typography variant="h4" align="center" mb={2}>
            Cadastre-se
          </Typography>
          <Typography variant="body2" align="center" mb={4}>
            Preencha os campos abaixo para criar sua conta.
          </Typography>

          <form onSubmit={handleSubmit}>
            <TextField
              fullWidth
              label="Nome completo"
              name="name"
              value={formState.name}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="CPF"
              name="cpf"
              value={formState.cpf}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Telefone"
              name="cellphone"
              value={formState.cellphone}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="E-mail"
              name="email"
              type="email"
              value={formState.email}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Senha"
              name="password"
              type="password"
              value={formState.password}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />
            <TextField
              fullWidth
              label="Confirme a senha"
              name="confirmPassword"
              type="password"
              value={formState.confirmPassword}
              onChange={handleChange}
              variant="outlined"
              margin="normal"
              required
            />

            <FormControlLabel
              control={
                <Checkbox
                  name="agreeStatute"
                  checked={formState.agreeStatute}
                  onChange={handleChange}
                />
              }
              label={
                <Box component="span">
                  Li e concordo com o{' '}
                  <Link to="/docs/EstatutoCPVL_2023.pdf" download target="_blank">
                    <Typography
                      component="span"
                      lineHeight={2}
                      fontSize="18px"
                      fontFamily="Flamenco, system-ui"
                      fontWeight="400"
                    >
                      Estatuto CPVL
                    </Typography>
                  </Link>
                </Box>
              }
            />
            <FormControlLabel
              control={
                <Checkbox
                  name="agreeRegimentoInterno"
                  checked={formState.agreeRegimentoInterno}
                  onChange={handleChange}
                />
              }
              label={
                <Box component="span">
                  Li e concordo com o{' '}
                  <Link
                    to="/docs/RegimentoInternoCPVL_2024.pdf"
                    download
                    target="_blank"
                  >
                    <Typography
                      component="span"
                      lineHeight={2}
                      fontSize="18px"
                      fontFamily="Flamenco, system-ui"
                      fontWeight="400"
                    >
                      Regimento Interno CPVL
                    </Typography>
                  </Link>
                </Box>
              }
            />
            <FormControlLabel
              control={
                <Checkbox
                  name="agreeLGPD"
                  checked={formState.agreeLGPD}
                  onChange={handleChange}
                />
              }
              label={
                <Box component="span">
                  Li e concordo com a{' '}
                  <Link to="/docs/lgpd_cpvl.pdf" download target="_blank">
                    <Typography
                      component="span"
                      lineHeight={2}
                      fontSize="18px"
                      fontFamily="Flamenco, system-ui"
                      fontWeight="400"
                    >
                      LGPD
                    </Typography>
                  </Link>
                </Box>
              }
            />
            {errors.length > 0 && (
              <Box sx={{ marginTop: 2 }}>
                {errors.map((error, index) => (
                  <Alert key={index} severity="error" sx={{ marginBottom: 1 }}>
                    {error}
                  </Alert>
                ))}
              </Box>
            )}

            <Box sx={{ marginTop: 2 }}>
              <Button
                variant="outlined"
                color="primary"
                type="button"
                fullWidth
                sx={{ marginBottom: 2 }}
                onClick={handleReset}
              >
                Limpar
              </Button>
              <Button variant="contained" type="submit" fullWidth sx={{ bgcolor: '#1f5388' }}>
                Cadastrar
              </Button>
            </Box>
          </form>

          <Box sx={{ marginTop: 3 }}>
            <Typography variant="body2" align="center">
              Já tem conta?{' '}
              <Link
                to="/login"
                style={{ textDecoration: 'underline', color: '#1f5388' }}
              >
                Faça login
              </Link>
            </Typography>
          </Box>
        </Box>
      </Box>
    );
  };
}
