import {
  Add as AddIcon,
  EmailOutlined as Email,
  Phone,
  ArrowForward as ArrowForwardIcon,
  FilterList as FilterIcon
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Divider,
  Link,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListSubheader,
  Typography,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Grid
} from '@mui/material';
import { blueGrey } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import * as Styled from './Pilots.styled';

interface IPilot {
  id?: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
  agreeStatute?: boolean;
  agreeRI?: boolean;
}

export const Pilots = () => {
  const [pilots, setPilots] = useState<Array<IPilot>>([]);
  const [filteredPilots, setFilteredPilots] = useState<Array<IPilot>>([]);
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [statusOptions, setStatusOptions] = useState<string[]>([]); // Estado para armazenar as opções de status
  const { data } = useFetch<Array<IPilot>>({ url: getURI(API.pilots) });
  const navigate = useNavigate();

  // Carregar os pilotos
  useEffect(() => {
    if (data && data.length) {
      setPilots(data);
      setFilteredPilots(data);
    }
  }, [data]);

  // Carregar as opções de status
  useEffect(() => {
    const fetchStatusOptions = async () => {
      try {
        const response = await fetch(getURI(API.pilots) + '/statusOptions');
        if (response.ok) {
          const statusData = await response.json();
          setStatusOptions(statusData);
        } else {
          console.error('Erro ao carregar opções de status');
        }
      } catch (error) {
        console.error('Erro na requisição', error);
      }
    };

    fetchStatusOptions();
  }, []);

  const handlePilotClick = (pilot: IPilot) => {
    const identifier = pilot.userId;
    navigate(`/pilots/${identifier}`);
  };

  const handleStatusChange = (event: React.ChangeEvent<{ value: unknown }>) => {
    const selectedStatus = event.target.value as string;
    setStatusFilter(selectedStatus);
    if (selectedStatus) {
      const filtered = pilots.filter(
        (pilot) => pilot.status === selectedStatus
      );
      setFilteredPilots(filtered);
    } else {
      setFilteredPilots(pilots); // Se não houver filtro, exibe todos
    }
  };

  return (
    <>
      <Box sx={{ display: 'flex', flexDirection: 'column' }}>
        <Box sx={{ mb : 1, padding: '15px' }}>
          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Typography color="text.primary">Pilotos</Typography>
          </Breadcrumbs>
        </Box>

        {/* Filtro de status */}
        <Box alignItems="center" border={1} padding={1} 
          sx={{ display: 'flex', justifyContent: 'space-around' }}
        >
          <Box border={1} textAlign={'center'}>
            <Box>
              <FormControl fullWidth>
                <InputLabel id="status-filter">
                  Filtro Status Cadastral
                </InputLabel>
                <Select
                  labelId="status-filter"
                  value={statusFilter}
                  label="Filtro Status Cadastral"
                  onChange={handleStatusChange}
                  sx={{ borderRadius: 3 }}
                >
                  <MenuItem value="">Todos</MenuItem>
                  {statusOptions.map((status) => (
                    <MenuItem key={status} value={status}>
                      {status.charAt(0).toUpperCase() + status.slice(1)}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Box>
          </Box>

          <Box border={1} textAlign={'center'}>
            <Box sx={{ width: auto }}>
              <FormControl fullWidth>
                <InputLabel id="status-filter">
                  Filtro Status Cadastral
                </InputLabel>
                <Select
                  labelId="status-filter"
                  value={statusFilter}
                  label="Filtro Status Cadastral"
                  onChange={handleStatusChange}
                  sx={{ borderRadius: 3 }}
                >
                  <MenuItem value="">Todos</MenuItem>
                  {statusOptions.map((status) => (
                    <MenuItem key={status} value={status}>
                      {status.charAt(0).toUpperCase() + status.slice(1)}
                    </MenuItem>
                  ))}
                </Select>
              </FormControl>
            </Box>
          </Box>
        </Box>


        {filteredPilots && (
          <List
            subheader={
              <ListSubheader
                component="div"
                id="nested-list-subheader"
                color="primary"
              >
                Total de {filteredPilots.length} piloto(s) em nossa base de
                dados.
              </ListSubheader>
            }
          >
            {filteredPilots.map((pilot) => (
              <Box key={pilot.userId}>
                <ListItem
                  disablePadding
                  secondaryAction={
                    <IconButton
                      edge="end"
                      aria-label="ver detalhes"
                      onClick={() => handlePilotClick(pilot)}
                    >
                      <ArrowForwardIcon />
                    </IconButton>
                  }
                >
                  <ListItemButton onClick={() => handlePilotClick(pilot)}>
                    <ListItemText
                      primary={`${pilot.firstName} ${pilot.lastName}`}
                      secondary={
                        <span style={{ display: 'flex', alignItems: 'center' }}>
                          <Phone sx={{ fontSize: '14px' }} />
                          <Typography
                            sx={{ paddingLeft: '5px', paddingTop: '2px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.cellphone}
                          </Typography>
                          <Email sx={{ fontSize: '15px', ml: 1 }} />
                          <Typography
                            sx={{ paddingLeft: '5px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.email}
                          </Typography>
                        </span>
                      }
                    />
                  </ListItemButton>
                </ListItem>
                <Divider />
              </Box>
            ))}
          </List>
        )}
      </Box>

      <Styled.AddButton color="primary" aria-label="add">
        <AddIcon />
      </Styled.AddButton>
    </>
  );
};
