import { Payment as PaymentIcon } from '@mui/icons-material';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Grid
} from '@mui/material';
import { blue } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import { PaymentQRCode } from '../PaymentQRCode';

interface IPaymentMonthly {
  id?: number;
  userId: number;
  ref_year: number;
  ref_month: number;
  amount: number | string;
  type: string;
  Description: string;
  date: Date | string;
}

export const PaymentMonthly = () => {
  const { userId } = useParams<{ userId: string }>();
  const [payments, setPayments] = useState<IPaymentMonthly[]>([]);

  const isUserIdValid = userId && !isNaN(parseInt(userId));
  const apiUrl = isUserIdValid
    ?
    getURI(`${API.paymentMonthly}/${userId}`)
    : undefined;

  const {
    data: paymentsData,
    error: paymentsError,
    loading: paymentsLoading
  } = useFetch<IPaymentMonthly[]>({
    method: 'GET',
    url: apiUrl
  });

  useEffect(() => {
    if (paymentsData) {
      setPayments(Array.isArray(paymentsData) ? paymentsData : []);
    } else if (paymentsError) {
      setPayments([]); // Clear payments on error
    }
  }, [paymentsData, paymentsError]);

  if (paymentsLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="400px"
      >
        <CircularProgress />
      </Box>
    );
  }

  // Format month
  const formatMonth = (month: number) => {
    const months = [
      'Janeiro',
      'Fevereiro',
      'Março',
      'Abril',
      'Maio',
      'Junho',
      'Julho',
      'Agosto',
      'Setembro',
      'Outubro',
      'Novembro',
      'Dezembro'
    ];
    return months[month - 1] || `Mês ${month}`;
  };

  // Parse amount
  const parseAmount = (amount: any): number => {
    if (!amount || amount === '') return 0;
    if (typeof amount === 'number') return amount;
    try {
      return parseFloat(amount.toString().replace(',', '.'));
    } catch (error) {
      console.error('Error parsing amount:', amount, error);
      return 0;
    }
  };

  // Calculate financial summary
  const getFinancialSummary = () => {
    const totalAmount = payments.reduce(
      (sum, p) => sum + parseAmount(p.amount),
      0
    );

    //   const monthlyStatus = payments
    //     .sort((a, b) => b.ref_year - a.ref_year || b.ref_month - a.ref_month)
    //     .map((payment) => ({
    //       year: payment.ref_year,
    //       month: payment.ref_month,
    //       monthName: formatMonth(payment.ref_month),
    //       amount: parseAmount(payment.amount),
    //       type: payment.type || 'N/A',
    //       description: payment.Description || 'N/A',
    //       date: payment.date
    //     }));

    return {
      total: payments.length,
      totalAmount,
      monthlyStatus,
    };
  };

  const financialSummary = getFinancialSummary();

  // Render invalid userId error
  if (!isUserIdValid) {
    console.error('Invalid userId:', userId);
    return (
      <Alert severity="error">
        ID de usuário inválido. Por favor, forneça um ID válido.
      </Alert>
    );
  }

  // Render loading state
  if (paymentsLoading) {
    return (
      <Box display="flex" justifyContent="center" p={3}>
        <CircularProgress />
      </Box>
    );
  }

  // Render error state
  if (paymentsError) {
    let errorMessage = 'Erro ao carregar histórico de pagamentos.';
    if (paymentsError.message.includes('not valid JSON')) {
      errorMessage =
        'Erro: A resposta do servidor não é um JSON válido. Verifique a configuração da API.';
    } else if (paymentsError.message === 'Unauthorized') {
      errorMessage = 'Acesso não autorizado. Por favor, faça login novamente.';
    } else if (paymentsError.message === 'Forbidden resource') {
      errorMessage =
        'Acesso proibido. Você não tem permissão para visualizar esses dados.';
    } else if (paymentsError.message) {
      errorMessage += ` Detalhes: ${paymentsError.message}`;
    }
    return (
      <Alert severity="error">
        {errorMessage} Tente novamente mais tarde ou contate o suporte.
      </Alert>
    );
  }

  // const monthCurrent = formatMonth(new Date().getMonth() + 1);
  const yearCurrent = new Date().getFullYear();
  const monthCurrent = new Date().getMonth() + 1;

  // Render empty state
  if (payments.length === 0) {
    return (
      <Alert severity="info">
        Nenhum registro de pagamento encontrado para este piloto.
      </Alert>
    );
  }

  return (
    <>
      <Card elevation={3} sx={{ mb: 3 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom color="primary">
            Resumo Financeiro
          </Typography>
          <Box mt={2}>
            <Grid container spacing={2}>
              <Grid item xs={12}>
                <Box textAlign="center" p={2} bgcolor={blue[50]} borderRadius={2}>
                  <Typography variant="h5" color={blue[600]}>
                    R$ {financialSummary.totalAmount.toFixed(2)}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Valor Total
                  </Typography>
                </Box>
              </Grid>
            </Grid>
          </Box>
          <Box mt={3}>
            <Typography variant="subtitle1" gutterBottom color="primary">
              Pagamentos dos Últimos Meses
            </Typography>
            <Box display="flex" flexWrap="wrap" gap={1}>
              {financialSummary.monthlyStatus.slice(0, 12).map((month, index) => (
                <Chip
                  key={`${month.year}-${month.month}`}
                  label={`${month.monthName.substring(0, 3)}/${month.year}`}
                  size="small"
                  sx={{
                    backgroundColor: blue[100],
                    color: blue[800],
                    '&:hover': { backgroundColor: blue[200] },
                  }}
                  title={`${month.monthName} ${month.year} - R$ ${month.amount.toFixed(2)}`}
                />
              ))}
            </Box>
            {financialSummary.monthlyStatus.length > 12 && (
              <Typography variant="caption" color="textSecondary" sx={{ mt: 1, display: 'block' }}>
                Mostrando os 12 meses mais recentes de {financialSummary.total} registros
              </Typography>
            )}
            {financialSummary.monthlyStatus.length === 0 && (
              <Alert severity="info" sx={{ mt: 1 }}>
                Nenhum registro encontrado.
              </Alert>
            )}
          </Box>
        </CardContent>
      </Card>

      <Card elevation={3}>
        <CardContent>
          <Box display="flex" alignItems="center" mb={3}>
            <PaymentIcon sx={{ fontSize: 32, color: blue[500], mr: 2 }} />
            <Typography variant="h5" component="h2">
              Histórico de Pagamentos - {userId}
            </Typography>
          </Box>
          <TableContainer component={Paper} elevation={0}>
            <Table>
              <TableHead>
                <TableRow>
                  <TableCell>
                    <strong>Mês</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Tipo</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Valor</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Descrição</strong>
                  </TableCell>
                  <TableCell>
                    <strong>Data</strong>
                  </TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {payments
                  .sort(
                    (a, b) =>
                      // b.ref_year - a.ref_year || b.ref_month - a.ref_month
                      a.ref_year - b.ref_year || a.ref_month - b.ref_month
                  )
                  .map((payment, index) => (
                    <TableRow key={`${payment.id || index}`} hover>
                      <TableCell>
                        {payment.ref_month === monthCurrent &&
                          payment.ref_year === yearCurrent ? (
                          <Typography
                            variant="body2"
                            color={blue[600]}
                            fontWeight={'bold'}
                          >
                            {formatMonth(payment.ref_month)} {payment.ref_year}
                          </Typography>
                        ) : (
                          <Typography variant="body2">
                            {formatMonth(payment.ref_month)}
                          </Typography>
                        )}
                      </TableCell>
                      <TableCell>
                        {payment.type ? (
                          <Typography variant="body2">
                            {payment.type}
                          </Typography>
                        ) : (
                          ''
                        )}
                      </TableCell>
                      <TableCell>
                        {parseAmount(payment.amount) > 0 ? (
                          <Chip
                            label={payment.amount}
                            size="medium"
                            variant="outlined"
                            color="success"
                            sx={{ fontWeight: 'bold' }}
                          />
                        ) : (
                          <Chip
                            label={payment.amount}
                            size="medium"
                            variant="outlined"
                            color="error"
                            sx={{ fontWeight: 'bold' }}
                          />
                        )}
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2">
                          {payment.Description &&
                            payment.Description.trim() !== ''
                            ? payment.Description
                            : ''}
                        </Typography>
                      </TableCell>
                      <TableCell>
                        <Typography variant="body2">
                          {payment.date
                            ? new Date(payment.date).toLocaleDateString('pt-BR')
                            : ''}
                        </Typography>
                      </TableCell>
                    </TableRow>
                  ))}
              </TableBody>
            </Table>

            <Table>
              <TableRow>
                <TableCell>
                  <Typography variant="body2">
                    R$ {financialSummary.totalAmount.toFixed(2)}
                  </Typography>
                </TableCell>
              </TableRow>



            </Table>
          </TableContainer>
        </CardContent>
      </Card>

      <Card elevation={3} sx={{ mt: 3 }}>
        <CardContent>
          <Box display="flex" alignItems="center" justifyContent="center" textAlign={'center'} mb={3}>
            <PaymentQRCode valor={Number(payments[0].amount)} />
          </Box>
        </CardContent>
      </Card >
    </>
  );
};
