import {
  Payment as PaymentIcon,
  Warning as WarningIcon,
  CheckCircle as CheckCircleIcon,
  Schedule as ScheduleIcon,
  Block as BlockIcon
} from '@mui/icons-material';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Grid,
  Chip,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress
} from '@mui/material';
import { blueGrey, blue, green, orange, red } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';

interface IPaymentMonthly {
  id?: number;
  userId: number;
  ref_year: number;
  ref_month: number;
  amount: number;
  status: string;
  type: string;
  Description: string;
  date: Date;
}

interface PaymentMonthlyProps {
  userId: string;
}

const PaymentMonthly: React.FC<PaymentMonthlyProps> = ({ userId }) => {
  const [payments, setPayments] = useState<IPaymentMonthly[]>([]);

  // ✅ Buscar pagamentos mensais do piloto
  const { data: paymentsData, loading: paymentsLoading, error: paymentsError } = useFetch<IPaymentMonthly[]>({
    url: getURI(`${API.paymentMonthly}?userId=${userId}`)
  });

  useEffect(() => {
    if (paymentsData) {
      setPayments(paymentsData);
    }
  }, [paymentsData]);

  // Função para formatar o mês
  const formatMonth = (month: number) => {
    const months = [
      'Janeiro', 'Fevereiro', 'Março', 'Abril', 'Maio', 'Junho',
      'Julho', 'Agosto', 'Setembro', 'Outubro', 'Novembro', 'Dezembro'
    ];
    return months[month - 1] || `Mês ${month}`;
  };

  // Função para converter valor monetário
  const parseAmount = (amount: any): number => {
    if (!amount || amount === '') return 0;
    if (typeof amount === 'number') return amount;
    // Converte string "50,00" para number 50.00
    return parseFloat(amount.toString().replace(',', '.'));
  };

  // Função para obter cor do status
  const getStatusColor = (status: string) => {
    switch (status?.trim()?.toLowerCase()) {
      case 'ativo':
        return green[500];
      case 'pendente':
        return orange[500];
      case 'inativo':
        return red[500];
      case 'trancado':
        return blueGrey[500];
      default:
        return blueGrey[300];
    }
  };

  // Função para obter ícone do status
  const getStatusIcon = (status: string) => {
    switch (status?.toLowerCase()) {
      case 'ativo':
        return <CheckCircleIcon sx={{ fontSize: 16, color: green[500] }} />;
      case 'pendente':
        return <ScheduleIcon sx={{ fontSize: 16, color: orange[500] }} />;
      case 'inativo':
        return <WarningIcon sx={{ fontSize: 16, color: red[500] }} />;
      case 'trancado':
        return <BlockIcon sx={{ fontSize: 16, color: blueGrey[500] }} />;
      default:
        return <WarningIcon sx={{ fontSize: 16, color: blueGrey[300] }} />;
    }
  };

  // Função para obter texto do status
  const getStatusText = (status: string) => {
    return status && status.trim() !== '' ? status : 'Não definido';
  };

  // Calcular resumo financeiro com status mês a mês
  const getFinancialSummary = () => {
    // Filtrar registros válidos (com status não vazio)
    const validPayments = payments.filter(p => p.status && p.status.trim() !== '');

    const activePayments = validPayments.filter(p => p.status === 'ativo').length;
    const pendingPayments = validPayments.filter(p => p.status === 'pendente').length;
    const inactivePayments = validPayments.filter(p => p.status === 'inativo').length;
    const lockedPayments = validPayments.filter(p => p.status === 'trancado').length;
    const undefinedPayments = payments.filter(p => !p.status || p.status.trim() === '').length;
    const totalAmount = validPayments.reduce((sum, p) => sum + parseAmount(p.amount), 0);

    // Criar array com status mês a mês (incluir apenas registros com dados relevantes)
    const monthlyStatus = payments
      .filter(payment => payment.status && payment.status.trim() !== '') // Filtrar apenas registros com status
      .sort((a, b) => b.ref_year - a.ref_year || b.ref_month - a.ref_month)
      .map(payment => ({
        year: payment.ref_year,
        month: payment.ref_month,
        monthName: formatMonth(payment.ref_month),
        status: payment.status,
        amount: parseAmount(payment.amount),
        type: payment.type,
        description: payment.Description,
        date: payment.date
      }));

    return {
      total: payments.length,
      valid: validPayments.length,
      active: activePayments,
      pending: pendingPayments,
      inactive: inactivePayments,
      locked: lockedPayments,
      undefined: undefinedPayments,
      totalAmount,
      monthlyStatus
    };
  };

  const financialSummary = getFinancialSummary();

  if (paymentsLoading) {
    return (
      <Box display="flex" justifyContent="center" p={3}>
        <CircularProgress />
      </Box>
    );
  }

  if (paymentsError) {
    return (
      <Alert severity="warning">
        Erro ao carregar histórico de pagamentos.
      </Alert>
    );
  }

  return (
    <>
      {/* Card Resumo Financeiro */}
      <Card elevation={3} sx={{ mb: 3 }}>
        <CardContent>
          <Typography variant="h6" gutterBottom color="primary">
            Resumo Financeiro
          </Typography>

          <Grid container spacing={2}>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={green[50]} borderRadius={2}>
                <Typography variant="h4" color={green[600]}>
                  {financialSummary.active}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Ativos
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={orange[50]} borderRadius={2}>
                <Typography variant="h4" color={orange[600]}>
                  {financialSummary.pending}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Pendentes
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={red[50]} borderRadius={2}>
                <Typography variant="h4" color={red[600]}>
                  {financialSummary.inactive}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Inativos
                </Typography>
              </Box>
            </Grid>
            <Grid item xs={6} sm={3}>
              <Box textAlign="center" p={2} bgcolor={blueGrey[50]} borderRadius={2}>
                <Typography variant="h4" color={blueGrey[600]}>
                  {financialSummary.locked}
                </Typography>
                <Typography variant="body2" color="textSecondary">
                  Trancados
                </Typography>
              </Box>
            </Grid>
          </Grid>

          {/* Card com valor total e registros indefinidos */}
          <Box mt={2}>
            <Grid container spacing={2}>
              <Grid item xs={6}>
                <Box textAlign="center" p={2} bgcolor={blue[50]} borderRadius={2}>
                  <Typography variant="h5" color={blue[600]}>
                    R$ {financialSummary.totalAmount.toFixed(2)}
                  </Typography>
                  <Typography variant="body2" color="textSecondary">
                    Valor Total
                  </Typography>
                </Box>
              </Grid>
              {financialSummary.undefined > 0 && (
                <Grid item xs={6}>
                  <Box textAlign="center" p={2} bgcolor={blueGrey[50]} borderRadius={2} border={1} borderColor={blueGrey[200]}>
                    <Typography variant="h5" color={blueGrey[600]}>
                      {financialSummary.undefined}
                    </Typography>
                    <Typography variant="body2" color="textSecondary">
                      Sem Status
                    </Typography>
                  </Box>
                </Grid>
              )}
            </Grid>
          </Box>

          {/* Status Mês a Mês */}
          <Box mt={3}>
            <Typography variant="subtitle1" gutterBottom color="primary">
              Status dos Últimos Meses
            </Typography>
            <Box display="flex" flexWrap="wrap" gap={1}>
              {financialSummary.monthlyStatus.slice(0, 12).map((month, index) => (
                <Chip
                  key={`${month.year}-${month.month}`}
                  label={`${month.monthName.substring(0, 3)}/${month.year}`}
                  size="small"
                  sx={{
                    backgroundColor: month.status === 'ativo' ? green[100] :
                      month.status === 'pendente' ? orange[100] :
                        month.status === 'inativo' ? red[100] :
                          month.status === 'trancado' ? blueGrey[100] :
                            blueGrey[50],
                    color: month.status === 'ativo' ? green[800] :
                      month.status === 'pendente' ? orange[800] :
                        month.status === 'inativo' ? red[800] :
                          month.status === 'trancado' ? blueGrey[800] :
                            blueGrey[600],
                    '&:hover': {
                      backgroundColor: month.status === 'ativo' ? green[200] :
                        month.status === 'pendente' ? orange[200] :
                          month.status === 'inativo' ? red[200] :
                            month.status === 'trancado' ? blueGrey[200] :
                              blueGrey[100]
                    }
                  }}
                  title={`${month.monthName} ${month.year} - R$ ${month.amount.toFixed(2)} (${getStatusText(month.status)})`}
                />
              ))}
            </Box>
            {financialSummary.monthlyStatus.length > 12 && (
              <Typography variant="caption" color="textSecondary" sx={{ mt: 1, display: 'block' }}>
                Mostrando os 12 meses mais recentes de {financialSummary.valid} registros válidos
              </Typography>
            )}
            {financialSummary.monthlyStatus.length === 0 && (
              <Alert severity="info" sx={{ mt: 1 }}>
                Nenhum registro com status definido encontrado.
              </Alert>
            )}
          </Box>
        </CardContent>
      </Card>

      {/* Card com histórico de pagamentos */}
      <Card elevation={3}>
        <CardContent>
          <Box display="flex" alignItems="center" mb={3}>
            <PaymentIcon sx={{ fontSize: 32, color: blue[500], mr: 2 }} />
            <Typography variant="h5" component="h2">
              Histórico de Pagamentos
            </Typography>
          </Box>

          {payments.length === 0 ? (
            <Alert severity="info">
              Nenhum registro de pagamento encontrado para este piloto.
            </Alert>
          ) : (
            <TableContainer component={Paper} elevation={0}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell><strong>Período</strong></TableCell>
                    <TableCell><strong>Tipo</strong></TableCell>
                    <TableCell><strong>Valor</strong></TableCell>
                    <TableCell><strong>Status</strong></TableCell>
                    <TableCell><strong>Descrição</strong></TableCell>
                    <TableCell><strong>Data</strong></TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {payments
                    .sort((a, b) => b.ref_year - a.ref_year || b.ref_month - a.ref_month)
                    .map((payment, index) => (
                      <TableRow key={`${payment.id || index}`} hover>
                        <TableCell>
                          <Typography variant="body2">
                            {formatMonth(payment.ref_month)} {payment.ref_year}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          {payment.type ? (
                            <Chip
                              label={payment.type}
                              size="small"
                              variant="outlined"
                            />
                          ) : (
                            <Typography variant="body2" color="textSecondary">-</Typography>
                          )}
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2" fontWeight="bold">
                            R$ {parseAmount(payment.amount).toFixed(2)}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Box display="flex" alignItems="center">
                            {getStatusIcon(payment.status)}
                            <Typography
                              variant="body2"
                              sx={{
                                ml: 1,
                                color: getStatusColor(payment.status),
                                textTransform: 'capitalize'
                              }}
                            >
                              {getStatusText(payment.status)}
                            </Typography>
                          </Box>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">
                            {payment.Description && payment.Description.trim() !== '' ? payment.Description : '-'}
                          </Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">
                            {/* {payment.date && payment.date !== '' ? new Date(payment.date).toLocaleDateString('pt-BR') : '-'} */}
                            {payment.date ? new Date(payment.date).toLocaleDateString('pt-BR') : '-'}
                          </Typography>
                        </TableCell>
                      </TableRow>
                    ))}
                </TableBody>
              </Table>
            </TableContainer>
          )}
        </CardContent>
      </Card>
    </>
  );
};

export default PaymentMonthly;
