import {
  Box,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  CircularProgress,
  Alert
} from '@mui/material';
import { useState, useEffect } from 'react';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import { useParams } from 'react-router-dom';

interface StatusPilotProps {
  userId: number;
  currentStatus: string;
  onStatusChange: (newStatus: string) => void;
}

export const StatusPilot = ({
  currentStatus,
  onStatusChange
}: StatusPilotProps) => {
  const { userId } = useParams<{ userId: string }>();
  const [status, setStatus] = useState(currentStatus);
  const [errorMessage, setErrorMessage] = useState<string>('');
  const [statusOptions, setStatusOptions] = useState<string[]>([]);

  // Busca os status válidos
  const { data: validStatusCadastral, loading: loadingStatus } = useFetch<string[]>({
    url: getURI(API.validStatusCadastral)
  });

  // Hook para fazer a requisição de atualização
  const { doFetch, loading, error } = useFetch({
    method: 'PATCH'
  });

  // Carrega os status válidos e mantém eles em memória
  useEffect(() => {
    if (validStatusCadastral && validStatusCadastral.length > 0) {
      setStatusOptions(validStatusCadastral);
    }
  }, [validStatusCadastral]);

  // Atualiza o status local quando o currentStatus mudar
  useEffect(() => {
    setStatus(currentStatus);
  }, [currentStatus]);

  useEffect(() => {
    if (error) {
      setErrorMessage('Erro ao atualizar status');
    }
  }, [error]);

  const handleUpdateStatus = async () => {
    try {
      const response = await doFetch({
        url: `${getURI(API.updateStatusPilot)}?userId=${userId}&status=${status}`,
        method: 'PATCH'
      });

      if (response) {
        alert('Status atualizado com sucesso!');
        onStatusChange(status);
        setErrorMessage('');
      }
    } catch (error) {
      setErrorMessage('Erro ao atualizar status');
    }
  };

  return (
    <Box display="flex" alignItems="center" gap={2} mt={1}>
      <FormControl size="small">
        <InputLabel>Status</InputLabel>
        <Select
          value={status}
          label="Status"
          onChange={(e) => setStatus(e.target.value)}
          sx={{ borderRadius: 3, minWidth: 150 }}
          disabled={loadingStatus || statusOptions.length === 0}
        >
          {statusOptions.length > 0 ? (
            statusOptions.map((statusOption) => (
              <MenuItem key={statusOption} value={statusOption}>
                {statusOption.charAt(0).toUpperCase() + statusOption.slice(1)}
              </MenuItem>
            ))
          ) : (
            <MenuItem value="" disabled>
              Carregando...
            </MenuItem>
          )}
        </Select>
      </FormControl>

      {loading ? (
        <CircularProgress size={24} />
      ) : (
        <Button
          variant="contained"
          color="primary"
          onClick={handleUpdateStatus}
          size="small"
          sx={{ borderRadius: 3 }}
          disabled={status === currentStatus}
        >
          Atualizar
        </Button>
      )}

      {errorMessage && (
        <Alert severity="error" sx={{ ml: 2 }}>
          {errorMessage}
        </Alert>
      )}
    </Box>
  );
};
