export const API = {
  profile: 'profile',
  login: 'login',
  pilots: 'pilots',
  paymentMonthly: 'paymentMonthly',
  status: 'pilots/status-list', // Updated to match backend endpoint
} as const;

export const getURI = (api: string, queryParams?: Record<string, string>) => {
  const base = `${process.env.REACT_APP_API_URI}/${api}`;
  if (queryParams) {
    const queryString = new URLSearchParams(queryParams).toString();
    return `${base}?${queryString}`;
  }
  return base;
};
