import React, { useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import QRCodePix from 'react-qrcode-pix';
import {
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  SelectChangeEvent,
  Typography,
  Button,
  Card,
  CardContent,
  Box,
  Chip
} from '@mui/material';

interface PaymentProps {
  totalMissing: number;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ totalMissing }) => {
  const { userId } = useParams<{ userId: string }>();
  const [selectedPaymentType, setSelectedPaymentType] = useState<string>('');
  const [pixCode, setPixCode] = useState<string>('');
  const [currentTransactionId, setCurrentTransactionId] = useState<string>('');

  // Generate unique transaction ID
  const generateTransactionId = useCallback((paymentType: string): string => {
    const timestamp = Date.now().toString();
    const userPart = userId ? userId.slice(-3) : '000'; // Last 3 chars of userId
    const typePart = paymentType.substring(0, 3).toUpperCase(); // First 3 chars of payment type

    // Format: CPVL + UserPart + TypePart + Timestamp(last 8 digits)
    // Total: 4 + 3 + 3 + 8 = 18 characters (within 25 limit)
    const transactionId = `CPVL${userPart}${typePart}${timestamp.slice(-8)}`;

    console.log('Generated Transaction ID:', transactionId, 'Length:', transactionId.length);
    return transactionId;
  }, [userId]);

  const getPaymentValue = (paymentType: string): number => {
    switch (paymentType) {
      case 'mensal': return 50;
      case 'trimestral': return 50 * 3;
      case 'semestral': return 50 * 6;
      case 'anual': return 50 * 12;
      default: return 0;
    }
  };

  const getDiscount = (paymentType: string): number => {
    switch (paymentType) {
      case 'trimestral': return 0.05;
      case 'semestral': return 0.10;
      case 'anual': return 0.15;
      default: return 0;
    }
  };

  const calculateFinalValue = (paymentType: string): number => {
    const baseValue = getPaymentValue(paymentType);
    const discount = getDiscount(paymentType);
    return Math.round(baseValue * (1 - discount) * 100) / 100;
  };

  const paymentType = (totalMissing: number | undefined): string[] => {
    if (totalMissing === undefined || totalMissing < 0 || totalMissing > 12) {
      return [];
    }

    const types: string[] = [];
    if (totalMissing > 0) types.push('mensal');
    if (totalMissing >= 3) types.push('trimestral');
    if (totalMissing >= 6) types.push('semestral');
    if (totalMissing === 12) types.push('anual');

    return types;
  };

  const handleChange = (event: SelectChangeEvent<string>) => {
    const newPaymentType = event.target.value;
    setSelectedPaymentType(newPaymentType);
    setPixCode(''); // Clear previous code

    // Generate new transaction ID for the new payment type
    if (newPaymentType) {
      const newTransactionId = generateTransactionId(newPaymentType);
      setCurrentTransactionId(newTransactionId);
    }
  };

  const copyToClipboard = async () => {
    if (pixCode) {
      try {
        await navigator.clipboard.writeText(pixCode);
        alert('C贸digo PIX copiado para a 谩rea de transfer锚ncia!');
      } catch (err) {
        const textArea = document.createElement('textarea');
        textArea.value = pixCode;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        alert('C贸digo PIX copiado!');
      }
    }
  };

  const availableTypes = paymentType(totalMissing);
  const currentValue = selectedPaymentType ? calculateFinalValue(selectedPaymentType) : 0;
  const currentDiscount = selectedPaymentType ? getDiscount(selectedPaymentType) : 0;
  const baseValue = selectedPaymentType ? getPaymentValue(selectedPaymentType) : 0;

  return (
    <Box>
      {totalMissing > 0 && (
        <Typography variant="body1" sx={{ mb: 2 }}>
          Faltam {totalMissing} meses de pagamento.
        </Typography>
      )}

      <FormControl fullWidth sx={{ mb: 3 }}>
        <InputLabel id="tipoPagamento">Tipo do pagamento</InputLabel>
        <Select
          labelId="tipoPagamento"
          value={selectedPaymentType}
          label="Tipo do pagamento"
          onChange={handleChange}
          disabled={availableTypes.length === 0}
        >
          {availableTypes.map((type: string, index: number) => {
            const amount = calculateFinalValue(type);
            const discount = getDiscount(type);
            const base = getPaymentValue(type);

            return (
              <MenuItem key={index} value={type}>
                <Box sx={{ display: 'flex', justifyContent: 'space-between', width: '100%' }}>
                  <span>{type.charAt(0).toUpperCase() + type.slice(1)}</span>
                  <Box sx={{ textAlign: 'right' }}>
                    {discount > 0 && (
                      <Chip 
                        label={`-${(discount * 100).toFixed(0)}%`} 
                        size="small" 
                        color="success" 
                        sx={{ mr: 1 }} 
                      />
                    )}
                    <span style={{ fontWeight: 'bold' }}>
                      R$ {amount.toFixed(2)}
                    </span>
                  </Box>
                </Box>
              </MenuItem>
            );
          })}
        </Select>
      </FormControl>

      {selectedPaymentType && currentTransactionId && (
        <Card sx={{ mt: 2 }}>
          <CardContent sx={{ textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>
              Pagamento PIX - {selectedPaymentType.charAt(0).toUpperCase() + selectedPaymentType.slice(1)}
            </Typography>

            {currentDiscount > 0 && (
              <Chip 
                label={`Economize ${(currentDiscount * 100).toFixed(0)}% - R$ ${(baseValue - currentValue).toFixed(2)}`}
                color="success"
                sx={{ mb: 2 }}
              />
            )}

            <Typography variant="h4" color="primary" sx={{ mb: 3 }}>
              R$ {currentValue.toFixed(2)}
            </Typography>

            {/* Debug Info - Remove after testing */}
            <Box sx={{ mb: 2, p: 1, backgroundColor: '#f5f5f5', borderRadius: 1 }}>
              <Typography variant="caption" display="block">
                Transaction ID: {currentTransactionId} (Length: {currentTransactionId.length})
              </Typography>
              <Typography variant="caption" display="block">
                Amount: {currentValue}
              </Typography>
            </Box>

            {/* QR Code */}
            <Box sx={{ mb: 3 }}>
              <QRCodePix
                pixkey={process.env.REACT_APP_PIX_KEY_CPVL || ''}
                merchant="CPVL"
                city="SAO PAULO"
                cep="01310100"
                amount={currentValue} // Use 'amount' instead of 'value'
                transactionId={currentTransactionId}
                message={`Mensalidade ${selectedPaymentType} - Usuario ${userId || 'N/A'}`}
                size={280}
                bgColor="#FFFFFF"
                fgColor="#000000"
                level="M"
                includeMargin={true}
                onPixCodeGenerated={setPixCode}
              />
            </Box>

            {pixCode && (
              <>
                <Typography variant="body2" sx={{ mb: 2 }}>
                  C贸digo PIX (Copia e Cola):
                </Typography>

                <Box sx={{ mb: 3 }}>
                  <textarea
                    value={pixCode}
                    readOnly
                    rows={3}
                    style={{
                      width: '100%',
                      padding: '12px',
                      fontSize: '11px',
                      fontFamily: 'monospace',
                      border: '1px solid #ddd',
                      borderRadius: '8px',
                      resize: 'none',
                      backgroundColor: '#f9f9f9'
                    }}
                  />
                </Box>

                <Button 
                  variant="contained" 
                  size="large"
                  onClick={copyToClipboard}
                  sx={{ 
                    mt: 1,
                    borderRadius: 2,
                    textTransform: 'none'
                  }}
                >
                   Copiar C贸digo PIX
                </Button>
              </>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default PaymentQRCode;
