import React from 'react';
import { Box, Typography, Divider, Grid, Link } from '@mui/material';
import dir_2025 from '../../assets/images/ata_diretoria_2024-2025.jpg';

interface DirectionItem {
  periodo: string;
  presidente: string;
  vice: string;
  tesoureiro: string;
  diretorTecnico: string;
  diretorSocial: string;
  ata: string;
}

const directionList: DirectionItem[] = [
  {
    periodo: '2024/2025',
    presidente: 'Gilberto Raposo',
    vice: 'Cássio Sarti',
    tesoureiro: 'Cláudio Lellis',
    diretorTecnico: 'Gustavo Borges',
    diretorSocial: 'Gustavo Garcia',
    ata: '/docs/ata_diretoria_2024-2025.pdf'
  },
  {
    periodo: '2021/2023',
    presidente: 'Cristiano Ricci',
    vice: 'Juliano de Vito',
    tesoureiro: 'Fernando C. Filho',
    diretorTecnico: 'Walter Moraes',
    diretorSocial: 'Leonardo Santos',
    ata: '/docs/ata_diretoria_2021-2023.pdf'
  },
  {
    periodo: '2020/2022',
    presidente: 'Renan Braz',
    vice: 'Rinaldo Marcondes',
    tesoureiro: 'Ricardo Galo',
    diretorTecnico: 'Cristiano Ricci',
    diretorSocial: 'Walter Moraes',
    ata: '/docs/ata_diretoria_2020-2022.pdf'
  }
];

export const Direction: React.FC = () => {
  return (
    <Box
      maxWidth={1200}
      mx="auto"
      mt={5}
      mb={30}
      color="#424242"
      px={2}
      sx={{ minHeight: '81vh' }}
    >
      <Typography variant="h4" mb={6} fontSize="42px" fontFamily="Flamenco, system-ui" fontWeight="300">
        Diretoria
      </Typography>

      <Box>
        <img src={dir_2025} alt="Diretoria 2025" style={{ width: '100%' }} />
      </Box>

      {directionList.map(
        ({
          periodo,
          presidente,
          vice,
          tesoureiro,
          diretorTecnico,
          diretorSocial,
          ata
        }) => (
          <Box key={periodo} mb={4}>
            <Grid container spacing={3} alignItems="flex-start">
              {/* Coluna 1 - Período */}
              <Grid item xs={12} sm={4}>
                <Typography
                  lineHeight={2}
                  variant="h5"
                  fontWeight="bold"
                  mb={1}
                >
                  {periodo}
                </Typography>
              </Grid>

              {/* Coluna 2 - Nomes */}
              <Grid item xs={12} sm={4}>
                <Typography lineHeight={2}>Presidente: {presidente}</Typography>
                <Typography lineHeight={2}>Vice Presidente: {vice}</Typography>
                <Typography lineHeight={2}>Tesoureiro: {tesoureiro}</Typography>
                <Typography lineHeight={2}>
                  Diretor Técnico: {diretorTecnico}
                </Typography>
                <Typography lineHeight={2}>
                  Diretor Social: {diretorSocial}
                </Typography>
              </Grid>

              {/* Coluna 3 - Link de Download */}
              <Grid item xs={12} sm={4} display="flex" justifyContent="center">
                {ata && (
                  <Link
                    href={ata}
                    download
                    target="_blank"
                    sx={{ textDecoration: 'none', color: '#1976d2' }}
                  >
                    <Typography lineHeight={2}>Ata</Typography>
                  </Link>
                )}
              </Grid>
            </Grid>
            <Divider sx={{ mt: 2 }} />
          </Box>
        )
      )}
    </Box>
  );
};
