const BASE_URI = process.env.REACT_APP_API_URI;

export const API = {
  profile: 'profile',
  login: 'login',
  pilots: 'pilots',
  pilotByUserId: 'pilots/byUserId',
  updatePilotStatus: 'pilots/statusPiloto',
  paymentMonthly: 'paymentMonthly',
  status: 'pilots/status-list'
} as const;

export const getURI = (api: string) => `${BASE_URI}/${api}`;

export const getPilotByUserIdURI = (userId: string | number) => 
  `${BASE_URI}/${API.pilotByUserId}/${userId}`;

export const getUpdatePilotStatusURI = (userId: string | number, status: string) => 
  `${BASE_URI}/${API.updatePilotStatus}?userId=${userId}&status=${encodeURIComponent(status)}`;
