import {
  Add as AddIcon,
  EmailOutlined as Email,
  Phone,
  ArrowForward as ArrowForwardIcon
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Divider,
  Link,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListSubheader,
  Typography,
  IconButton
} from '@mui/material';
import { blueGrey } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import * as Styled from './Pilots.styled';

interface IPilot {
  id?: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  agreeStatute?: boolean;
  agreeRI?: boolean;
}

const Pilots = () => {
  const [pilots, setPilots] = useState<Array<IPilot>>();
  const { data } = useFetch<Array<IPilot>>({ url: getURI(API.pilots) });
  const navigate = useNavigate();

  useEffect(() => {
    if (data && data.length) {
      setPilots(data);
    }
  }, [data]);

  const handlePilotClick = (pilot: IPilot) => {
    // Usar ID se disponível, senão usar CPF como identificador
    // const identifier = pilot.userId || pilot.cpf;
    const identifier = pilot.userId;
    console.log('')
    navigate(`/pilots/${identifier}`);
  };

  return (
    <>
      <Box>
        <Box sx={{ padding: '15px' }}>
          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Typography color="text.primary">Pilotos</Typography>
          </Breadcrumbs>
        </Box>
        {pilots && (
          <List
            subheader={
              <ListSubheader
                component="div"
                id="nested-list-subheader"
                color="primary"
              >
                Total de {pilots.length} piloto(s) em nossa base de dados.
              </ListSubheader>
            }
          >
            {pilots.map((pilot, userId) => (
              <Box key={userId}>
                <ListItem
                  disablePadding
                  secondaryAction={
                    <IconButton
                      edge="end"
                      aria-label="ver detalhes"
                      onClick={() => handlePilotClick(pilot)}
                    >
                      <ArrowForwardIcon />
                    </IconButton>
                  }
                >
                  <ListItemButton onClick={() => handlePilotClick(pilot)}>
                    {' '}
                    {/* ✅ Clique no item inteiro */}
                    <ListItemText
                      primary={`${pilot.firstName} ${pilot.lastName}`}
                      secondary={
                        <span style={{ display: 'flex', alignItems: 'center' }}>
                          <Phone sx={{ fontSize: '14px' }} />
                          <Typography
                            sx={{ paddingLeft: '5px', paddingTop: '2px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.cellphone}
                          </Typography>
                          <Email sx={{ fontSize: '15px', ml: 1 }} />
                          <Typography
                            sx={{ paddingLeft: '5px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.email}
                          </Typography>
                        </span>
                      }
                      key={pilot.userId}
                    />
                  </ListItemButton>
                </ListItem>
                <Divider />
              </Box>
            ))}
          </List>
        )}
      </Box>
      <Styled.AddButton color="primary" aria-label="add">
        <AddIcon />
      </Styled.AddButton>
    </>
  );
};

export default Pilots;
