import React, { useState } from 'react';
import { TextField, Button, Box, Typography, IconButton, Snackbar } from '@mui/material';
// import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import QRCode from 'qrcode.react';
import { QrCodePix } from 'qrcode-pix';

export const QrCode: React.FC = (userId) => {
  const [pixKey, setPixKey] = useState<string>('');
  const [value, setValue] = useState<string>('');
  const [pixCode, setPixCode] = useState<string>('');
  const [qrCodeUrl, setQrCodeUrl] = useState<string>('');
  const [openSnackbar, setOpenSnackbar] = useState<boolean>(false);
  const [loading, setLoading] = useState<boolean>(false);

  const handleGeneratePix = async () => {
    if (!pixKey || !value) {
      alert('Por favor, preencha a chave PIX e o valor.');
      return;
    }

    setLoading(true);
    try {
      const qrCodePix = QrCodePix({
        version: '01',
        key: pixKey,
        name: 'Seu Nome',
        city: 'SAO PAULO',
        transactionId: 'TX' + Math.random().toString(36).substr(2, 9),
        value: parseFloat(value),
      });

      const payload = qrCodePix.payload();
      const base64Image = await qrCodePix.base64();

      setPixCode(payload);
      setQrCodeUrl(base64Image);
    } catch (error) {
      console.error('Error generating PIX:', error);
      alert('Erro ao gerar o QR Code PIX. Verifique os dados informados.');
    } finally {
      setLoading(false);
    }
  };

  const handleCopyToClipboard = async () => {
    if (qrCodeUrl) {
      await navigator.clipboard.writeText(qrCodeUrl);
    } else {
      await navigator.clipboard.writeText(pixCode);
    }
    setOpenSnackbar(true);
  };

  const handleCloseSnackbar = () => {
    setOpenSnackbar(false);
  };

  return (
    <Box sx={{ p: 3, maxWidth: 500, margin: 'auto' }}>
      <Typography variant="h5" gutterBottom>
        Gerador de QR Code PIX
      </Typography>
      <TextField
        label="Chave PIX"
        value={pixKey}
        onChange={(e) => setPixKey(e.target.value)}
        fullWidth
        margin="normal"
        placeholder="Digite sua chave PIX (CPF, e-mail, telefone, etc.)"
      />
      <TextField
        label="Valor (R$)"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        fullWidth
        margin="normal"
        type="number"
        // step="0.01"
        placeholder="Digite o valor"
        inputProps={{ min: "0.01" }}
      />
      <Button
        variant="contained"
        color="primary"
        onClick={handleGeneratePix}
        sx={{ mt: 2 }}
        disabled={loading}
      >
        {loading ? 'Gerando...' : 'Gerar QR Code'}
      </Button>
{/*       
