import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Card,
  CardContent,
  CardHeader,
  Typography,
  FormControl,
  InputLabel,
  Select,
  MenuItem,
  Button,
  Alert,
  Box,
  CircularProgress,
  Chip,
  Divider,
  Avatar,
  Grid
} from '@mui/material';
import {
  Person as PersonIcon,
  Email as EmailIcon,
  Phone as PhoneIcon,
  Badge as BadgeIcon,
  Save as SaveIcon,
  ArrowBack as ArrowBackIcon
} from '@mui/icons-material';
import { SelectChangeEvent } from '@mui/material/Select';
import { useFetch } from '../../hooks/';
import { getPilotByUserIdURI, getUpdatePilotStatusURI } from '../../services/'; // Ajuste o caminho

interface User {
  id: number;
  username: string;
  email: string;
}

interface Pilot {
  id: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
  user: User;
}

const statusOptions = [
  { value: 'filiado', label: 'Filiado', color: 'success' as const },
  { value: 'expulso', label: 'Expulso', color: 'error' as const },
  { value: 'inativo', label: 'Inativo', color: 'error' as const },
  { value: 'pendente', label: 'Pendente', color: 'warning' as const },
  { value: 'suspenso', label: 'Expulso', color: 'error' as const },
  { value: 'trancado', label: 'Trancado', color: 'error' as const }
];

export const UpdatePilotStatus: React.FC = () => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();

  const [selectedStatus, setSelectedStatus] = useState<string>('');
  const [successMessage, setSuccessMessage] = useState<string>('');

  // Hook para buscar dados do piloto
  const {
    data: pilot,
    loading: loadingPilot,
    error: errorPilot
  } = useFetch<Pilot>({
    method: 'GET',
    url: userId ? getPilotByUserIdURI(userId) : undefined
  });

  // Hook para atualizar status
  const {
    doFetch: updateStatus,
    loading: updating,
    error: updateError
  } = useFetch<Pilot>({
    method: 'PATCH'
  });

  // Define o status inicial quando os dados do piloto são carregados
  useEffect(() => {
    if (pilot) {
      setSelectedStatus(pilot.status || '');
    }
  }, [pilot]);

  // Limpa mensagens de sucesso após 5 segundos
  useEffect(() => {
    if (successMessage) {
      const timer = setTimeout(() => {
        setSuccessMessage('');
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [successMessage]);

  const handleStatusChange = (event: SelectChangeEvent) => {
    setSelectedStatus(event.target.value);
    setSuccessMessage(''); // Limpa mensagem de sucesso ao alterar status
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!userId) {
      return;
    }

    try {
      await updateStatus({
        url: getUpdatePilotStatusURI(userId, selectedStatus),
        method: 'PATCH'
      });

      setSuccessMessage('Status do piloto atualizado com sucesso!');
    } catch (error) {
      console.error('Erro ao atualizar status:', error);
    }
  };

  const handleGoBack = () => {
    navigate(-1); // Volta para a página anterior
  };

  const getStatusChip = (status: string) => {
    const statusConfig = statusOptions.find(
      (option) => option.value === status
    );
    return (
      <Chip
        label={statusConfig!.label || 'Desconhecido'}
        color={statusConfig!.color || 'default'}
        size="small"
        variant="filled"
      />
    );
  };

  if (loadingPilot) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="400px"
      >
        <CircularProgress />
        <Typography variant="h6" sx={{ ml: 2 }}>
          Carregando dados do piloto...
        </Typography>
      </Box>
    );
  }

  if (errorPilot) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="error" sx={{ mb: 2 }}>
          Erro ao carregar dados do piloto: {errorPilot.message}
        </Alert>
        <Button
          variant="outlined"
          startIcon={<ArrowBackIcon />}
          onClick={handleGoBack}
        >
          Voltar
        </Button>
      </Box>
    );
  }

  if (!pilot) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="warning" sx={{ mb: 2 }}>
          Piloto não encontrado
        </Alert>
        <Button
          variant="outlined"
          startIcon={<ArrowBackIcon />}
          onClick={handleGoBack}
        >
          Voltar
        </Button>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3, maxWidth: 800, mx: 'auto' }}>
      {/* Header */}
      <Box sx={{ mb: 3, display: 'flex', alignItems: 'center', gap: 2 }}>
        <Button
          variant="outlined"
          startIcon={<ArrowBackIcon />}
          onClick={handleGoBack}
          size="small"
        >
          Voltar
        </Button>
        <Typography variant="h4" component="h1">
          Atualizar Status do Piloto
        </Typography>
      </Box>

      {/* Informações do Piloto */}
      <Card sx={{ mb: 3 }}>
        <CardHeader
          avatar={
            <Avatar sx={{ bgcolor: 'primary.main' }}>
              <PersonIcon />
            </Avatar>
          }
          title={`${pilot.firstName} ${pilot.lastName}`}
          subheader={`ID do Usuário: ${pilot.userId}`}
          action={getStatusChip(pilot.status)}
        />
        <CardContent>
          <Grid container spacing={2}>
            <Grid item xs={12} sm={6}>
              <Box
                sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}
              >
                <EmailIcon color="action" fontSize="small" />
                <Typography variant="body2" color="text.secondary">
                  E-mail:
                </Typography>
                <Typography variant="body2">{pilot.email}</Typography>
              </Box>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Box
                sx={{ display: 'flex', alignItems: 'center', gap: 1, mb: 1 }}
              >
                <PhoneIcon color="action" fontSize="small" />
                <Typography variant="body2" color="text.secondary">
                  Celular:
                </Typography>
                <Typography variant="body2">{pilot.cellphone}</Typography>
              </Box>
            </Grid>
            <Grid item xs={12} sm={6}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                <BadgeIcon color="action" fontSize="small" />
                <Typography variant="body2" color="text.secondary">
                  CPF:
                </Typography>
                <Typography variant="body2">{pilot.cpf}</Typography>
              </Box>
            </Grid>
          </Grid>
        </CardContent>
      </Card>

      {/* Formulário de Atualização */}
      <Card>
        <CardHeader
          title="Alterar Status"
          subheader="Selecione o novo status para o piloto"
        />
        <Divider />
        <CardContent>
          {successMessage && (
            <Alert severity="success" sx={{ mb: 3 }}>
              {successMessage}
            </Alert>
          )}

          {updateError && (
            <Alert severity="error" sx={{ mb: 3 }}>
              Erro ao atualizar status: {updateError.message}
            </Alert>
          )}

          <form onSubmit={handleSubmit}>
            <FormControl fullWidth sx={{ mb: 3 }}>
              <InputLabel id="status-select-label">Status</InputLabel>
              <Select
                labelId="status-select-label"
                id="status-select"
                value={selectedStatus}
                label="Status"
                onChange={handleStatusChange}
                disabled={updating}
              >
                {statusOptions.map((option) => (
                  <MenuItem key={option.value} value={option.value}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <Chip
                        label={option.label}
                        color={option.color}
                        size="small"
                        variant="outlined"
                      />
                    </Box>
                  </MenuItem>
                ))}
              </Select>
            </FormControl>

            <Box sx={{ display: 'flex', gap: 2 }}>
              <Button
                type="submit"
                variant="contained"
                startIcon={
                  updating ? <CircularProgress size={20} /> : <SaveIcon />
                }
                disabled={updating || selectedStatus === pilot.status}
                fullWidth
              >
                {updating ? 'Atualizando...' : 'Atualizar Status'}
              </Button>
            </Box>
          </form>
        </CardContent>
      </Card>
    </Box>
  );
};
