import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Card,
  CardContent,
  Grid,
  TextField,
  InputAdornment,
  Tab,
  Tabs,
  CircularProgress,
  Alert,
  Avatar,
  Tooltip,
  IconButton
} from '@mui/material';
import {
  Search as SearchIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  Person as PersonIcon,
  Email as EmailIcon,
  Phone as PhoneIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';
import { green, red, orange } from '@mui/material/colors';

interface PilotStatusData {
  id: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  email: string;
  cellphone: string;
  status: string;
  isAuthorized: boolean;
  lastPaymentMonth: number | null;
  lastPaymentYear: number | null;
  lastPaymentAmount: number | null;
  lastPaymentDate: string | null;
  monthsUpToDate: number;
}

interface StatusListResponse {
  success: boolean;
  summary?: {
    total: number;
    authorized: number;
    unauthorized: number;
    authorizationRate: string;
  };
  data?: {
    authorized: PilotStatusData[];
    unauthorized: PilotStatusData[];
    all: PilotStatusData[];
  };
  count?: number;
  currentMonth: number;
  currentYear: number;
}

export const StatusList = () => {
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [data, setData] = useState<StatusListResponse | null>(null);
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [currentTab, setCurrentTab] = useState<number>(0);

  const monthNames = [
    'Jan',
    'Fev',
    'Mar',
    'Abr',
    'Mai',
    'Jun',
    'Jul',
    'Ago',
    'Set',
    'Out',
    'Nov',
    'Dez'
  ];

  const fetchStatusList = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/status-list/all', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Erro ao carregar dados');
      }

      const result: StatusListResponse = await response.json();
      setData(result);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Erro desconhecido');
    } finally {
      setLoading(false);
    }
  };

