const BASE_URI = process.env.REACT_APP_API_URI;

export const API = {
  profile: 'profile',
  login: 'login',
  pilots: 'pilots',
  status: 'pilots/status-list'
} as const;

export const getURI = (api: string) => `${BASE_URI}/${api}`;

export const getPilotByUserIdURI = (userId: string | number) =>
  `${BASE_URI}/${API.pilotByUserId}/${userId}`;

export const getUpdatePilotStatusURI = (
  userId: string | number,
  status: string
) => `${BASE_URI}/${API.updateStatusPilot}?userId=${userId}&status=${status}`;

export const getUpdatePaymentAmountURI = (id: number | string) =>
  `${BASE_URI}/${API.updatePaymentAmount}/${id}/amount`;

export const createPaymentMonthly = (
  userId: string,
  amount: number,
  ref_year: string,
  ref_month: number,
  type?: string,
  description?: string,
  status?: string,
  date?: Date
) =>
  `${BASE_URI}/${API.createPaymentMonthly}?userId=${userId}&amount=${amount}&ref_year=${ref_year}&ref_month=${ref_month}&type=${type}&description=${description}&status=${status}&date=${date}`;
