import {
  ArrowBack as ArrowBackIcon,
  Person as PersonIcon,
  Badge as BadgeIcon
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Link,
  Typography,
  Card,
  CardContent,
  Grid,
  IconButton,
  Chip,
  Divider,
  CircularProgress,
  Alert,
  Select,
  MenuItem,
  Button
} from '@mui/material';
import { blueGrey, blue } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useNavigate, useParams } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI, getUpdatePilotStatusURI } from '../../services';
import { PaymentMonthly } from '../../components/PaymentMonthly';


interface IPilotDetails {
  id?: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
}

export const PilotDetails = () => {
  const { userId } = useParams<{ userId: string }>();
  const navigate = useNavigate();
  const [pilot, setPilot] = useState<IPilotDetails | null>(null);

  const {
    data: pilotData,
    error: pilotError,
    loading: pilotLoading
  } = useFetch<IPilotDetails>({
    url: getURI(`${API.pilots}/${userId}`)
  });

  const { doFetch, loading } = useFetch<{ pilot: IPilotDetails }>({
    method: 'PATCH'
  });

  const [status, setStatus] = useState(pilot!.status || '');

  useEffect(() => {
    if (pilotData) {
      setPilot(pilotData);
      setStatus(pilotData.status);
    }
  }, [pilotData]);

  const handleSaveStatus = async () => {
    if (!userId || !status) return;
    setLoadingSave(true);

    const result = await doFetch({
      url: getUpdatePilotStatusURI(userId, status),
      method: 'PATCH'
    });

    if (result!.pilot) {
      setPilot(result!.pilot); // atualiza o estado local
    }
  };

  const handleBackClick = () => {
    navigate('/pilots');
  };

  if (pilotLoading) {
    return (
      <Box
        display="flex"
        justifyContent="center"
        alignItems="center"
        minHeight="400px"
      >
        <CircularProgress />
      </Box>
    );
  }

  if (pilotError || !pilot) {
    return (
      <Box sx={{ padding: '15px' }}>
        <Alert severity="error">
          Erro ao carregar detalhes do piloto. Piloto não encontrado.
        </Alert>
      </Box>
    );
  }

  return (
    <Box>
      <Box sx={{ padding: '15px' }}>
        <Box display="flex" alignItems="center" mb={2}>
          <IconButton onClick={handleBackClick} sx={{ mr: 1 }}>
            <ArrowBackIcon />
          </IconButton>

          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Link
              underline="hover"
              color="inherit"
              onClick={handleBackClick}
              sx={{ cursor: 'pointer' }}
            >
              Pilotos
            </Link>
            <Typography color="text.primary">
              {pilot.firstName} {pilot.lastName}
            </Typography>
          </Breadcrumbs>
        </Box>

        {/* Card com informações do piloto */}
        <Card elevation={3} sx={{ mb: 3 }}>
          <CardContent>
            <Box display="flex" alignItems="center" mb={3}>
              <PersonIcon sx={{ fontSize: 40, color: blue[500], mr: 2 }} />
              <Box>
                <Typography variant="h4" component="h1">
                  {pilot.firstName} {pilot.lastName}
                </Typography>
                <Chip
                  label={pilot.status}
                  color="primary"
                  size="small"
                  sx={{ mt: 1 }}
                />
                <hr />
                <Select
                  label="Status"
                  size="small"
                  value={status}
                  onChange={(e) => setStatus(e.target.value)}
                  disabled={loading}
                >
                  <MenuItem value="ativo">Ativo</MenuItem>
                  <MenuItem value="inativo">Inativo</MenuItem>
                  <MenuItem value="pendente">Pendente</MenuItem>
                  <MenuItem value="trancado">Trancado</MenuItem>
                </Select>
                <Button
                  variant="contained"
                  sx={{ ml: 2 }}
                  onClick={handleSaveStatus}
                  disabled={loadingSave}
                >
                  {loadingSave ? 'Salvando...' : 'Salvar'}
                </Button>
              </Box>
            </Box>

            <Divider sx={{ mb: 3 }} />

            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <Typography variant="h6" gutterBottom color="primary">
                  Informações Pessoais:
                </Typography>

                <Box display="flex" alignItems="center" mb={2}>
                  <BadgeIcon sx={{ mr: 1, color: blueGrey[500] }} />
                  <Box>
                    <Typography variant="body2" color="textSecondary">
                      CPF
                    </Typography>
                    <Typography variant="body1">{pilot.cpf}</Typography>
                  </Box>
                </Box>

                <Box display="flex" alignItems="center" mb={2}>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    sx={{ mr: 2, minWidth: '80px' }}
                  >
                    Celular:
                  </Typography>
                  <Typography variant="body1">{pilot.cellphone}</Typography>
                </Box>

                <Box display="flex" alignItems="center" mb={2}>
                  <Typography
                    variant="body2"
                    color="textSecondary"
                    sx={{ mr: 2, minWidth: '80px' }}
                  >
                    E-mail:
                  </Typography>
                  <Typography variant="body1">{pilot.email}</Typography>
                </Box>
              </Grid>
            </Grid>
          </CardContent>
        </Card>

        <Card elevation={3} sx={{ mb: 3 }}>
          {userId && <PaymentMonthly />}
        </Card>
      </Box>
    </Box>
  );
};
