import { Payment as PaymentIcon } from '@mui/icons-material';
import {
  Box,
  Typography,
  Card,
  CardContent,
  Chip,
  Alert,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Paper,
  CircularProgress,
  Grid,
  Accordion,
  AccordionSummary,
  AccordionDetails,
  Skeleton,
} from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { blue, blueGrey, green, lime } from '@mui/material/colors';
import { useEffect, useMemo, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import { PaymentQRCode } from '../PaymentQRCode';

// Define interfaces
interface IPaymentMonthly {
  id?: number;
  userId: number;
  ref_year: number;
  ref_month: number;
  amount: number | string;
  type: string;
  Description: string;
  date: Date | string;
}

// Custom hook for financial summary
const useFinancialSummary = (payments: IPaymentMonthly[]) => {
  const months = [
    'Janeiro',
    'Fevereiro',
    'Março',
    'Abril',
    'Maio',
    'Junho',
    'Julho',
    'Agosto',
    'Setembro',
    'Outubro',
    'Novembro',
    'Dezembro',
  ];

  const parseAmount = (amount: any): number => {
    if (!amount || amount === '') return 0;
    if (typeof amount === 'number') return amount;
    try {
      return parseFloat(amount.toString().replace(',', '.'));
    } catch (error) {
      console.error('Error parsing amount:', amount, error);
      return 0;
    }
  };

  const formatDate = (date: Date | string): string => {
    if (!date) return '';
    return new Date(date).toLocaleDateString('pt-BR', {
      day: '2-digit',
      month: '2-digit',
      year: 'numeric',
    });
  };

  const financialSummary = useMemo(() => {
    const totalAmount = payments.reduce((sum, p) => sum + parseAmount(p.amount), 0);
    const monthlyStatus = payments
      .sort((a, b) => a.ref_year - b.ref_year || a.ref_month - b.ref_month)
      .map((payment) => ({
        year: payment.ref_year,
        month: payment.ref_month,
        monthName: months[payment.ref_month - 1] || `Mês ${payment.ref_month}`,
        amount: parseAmount(payment.amount),
        type: payment.type || 'N/A',
        description: payment.Description || 'N/A',
        date: formatDate(payment.date),
      }));

    return {
      total: payments.length,
      totalAmount,
      monthlyStatus,
    };
  }, [payments]);

  return financialSummary;
};

// Reusable Chip component
const StyledChip = ({
  label,
  isCurrent,
  variant = 'outlined',
  sx,
}: {
  label: string | number;
  isCurrent?: boolean;
  variant?: 'filled' | 'outlined';
  sx?: object;
}) => (
  <Chip
    label={label}
    size="small"
    variant={variant}
    sx={{
      width: 150,
      fontWeight: isCurrent ? 'bold' : 'normal',
      backgroundColor: isCurrent ? blue[100] : blue[50],
      color: isCurrent ? blue[800] : blue[700],
      '&:hover': { backgroundColor: isCurrent ? blue[200] : blue[100] },
      ...sx,
    }}
  />
);

export const PaymentMonthly = () => {
  const { userId } = useParams<{ userId: string }>();
  const [payments, setPayments] = useState<IPaymentMonthly[]>([]);
  const isUserIdValid = userId && !isNaN(parseInt(userId));
  const apiUrl = isUserIdValid ? getURI(`${API.paymentMonthly}/${userId}`) : undefined;

  const {
    data: paymentsData,
    error: paymentsError,
    loading: paymentsLoading,
  } = useFetch<IPaymentMonthly[]>({
    method: 'GET',
    url: apiUrl,
  });

  useEffect(() => {
    if (paymentsData) {
      setPayments(Array.isArray(paymentsData) ? paymentsData : []);
    } else if (paymentsError) {
      setPayments([]);
    }
  }, [paymentsData, paymentsError]);

  const financialSummary = useFinancialSummary(payments);
  const currentYear = new Date().getFullYear();
  const currentMonth = new Date().getMonth() + 1;

  // Error message mapping
  const errorMessages: Record<string, string> = {
    'not valid JSON':
      'Erro: A resposta do servidor não é um JSON válido. Verifique a configuração da API.',
    Unauthorized: 'Acesso não autorizado. Por favor, faça login novamente.',
    'Forbidden resource':
      'Acesso proibido. Você não tem permissão para visualizar esses dados.',
    default: 'Erro ao carregar histórico de pagamentos. Tente novamente mais tarde ou contate o suporte.',
  };

  if (!isUserIdValid) {
    console.error('Invalid userId:', userId);
    return <Alert severity="error">ID de usuário inválido. Por favor, forneça um ID válido.</Alert>;
  }

  if (paymentsLoading) {
    return (
      <Box p={3}>
        <Skeleton variant="rectangular" height={60} sx={{ mb: 2 }} />
        <Skeleton variant="rectangular" height={300} />
      </Box>
    );
  }

  if (paymentsError) {
    const errorMessage =
      errorMessages[paymentsError.message] || errorMessages.default;
    return <Alert severity="error">{errorMessage}</Alert>;
  }

  if (payments.length === 0) {
    return (
      <Alert severity="info">Nenhum registro de pagamento encontrado para este piloto.</Alert>
    );
  }

  return (
    <Grid container spacing={3}>
      <Grid item xs={12}>
        <Card elevation={3}>
          <Accordion defaultExpanded>
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              aria-controls="Painel"
              id="panel1-header"
            >
              <Box display="flex" alignItems="center">
                <PaymentIcon sx={{ fontSize: 32, color: blue[500], mr: 2 }} />
                <Typography variant="h5" color="primary">
                  Resumo Financeiro
                </Typography>
              </Box>
            </AccordionSummary>
            <AccordionDetails>
              <TableContainer component={Paper} elevation={0}>
                <Table>
                  <TableHead>
                    <TableRow>
                      <TableCell sx={{ color: blue[600], fontWeight: 'bold' }}>Mês</TableCell>
                      <TableCell sx={{ color: blue[600], fontWeight: 'bold' }}>Tipo</TableCell>
                      <TableCell sx={{ color: blue[600], fontWeight: 'bold' }}>Valor</TableCell>
                      <TableCell sx={{ color: blue[600], fontWeight: 'bold' }}>
                        Descrição
                      </TableCell>
                      <TableCell sx={{ color: blue[600], fontWeight: 'bold' }}>Data</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {financialSummary.monthlyStatus.map((payment, index) => (
                      <TableRow key={`${payment.year}-${payment.month}-${index}`} hover>
                        <TableCell>
                          <StyledChip
                            label={payment.monthName}
                            isCurrent={
                              payment.month === currentMonth && payment.year === currentYear
                            }
                            title={`${payment.year}`}
                          />
                        </TableCell>
                        <TableCell>
                          <StyledChip
                            label={payment.type}
                            sx={{
                              backgroundColor: lime[50],
                              color: lime[800],
                              '&:hover': { backgroundColor: lime[100] },
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <StyledChip
                            label={`R$ ${payment.amount.toFixed(2)}`}
                            sx={{
                              backgroundColor: payment.amount > 0 ? green[50] : blueGrey[50],
                              color: payment.amount > 0 ? green[800] : blueGrey[300],
                              '&:hover': {
                                backgroundColor: payment.amount > 0 ? green[100] : blueGrey[100],
                              },
                            }}
                          />
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">{payment.description}</Typography>
                        </TableCell>
                        <TableCell>
                          <Typography variant="body2">{payment.date}</Typography>
                        </TableCell>
                      </TableRow>
                    ))}
                    <TableRow>
                      <TableCell colSpan={5}>
                        <Box textAlign="center" p={2} bgcolor={blue[50]} borderRadius={2}>
                          <Typography variant="h5" color={blue[600]}>
                            R$ {financialSummary.totalAmount.toFixed(2)}
                          </Typography>
                          <Typography variant="body2" color="textSecondary">
                            Valor Total
                          </Typography>
                        </Box>
                      </TableCell>
                    </TableRow>
                  </TableBody>
                </Table>
              </TableContainer>
            </AccordionDetails>
          </Accordion>
        </Card>
      </Grid>

      <Grid item xs={12} md={6} sx={{ mx: 'auto' }}>
        <Card elevation={3}>
          <CardContent>
            <Box display="flex" justifyContent="center" textAlign="center">
              <PaymentQRCode valor={Number(financialSummary.monthlyStatus[0]!.amount || 0)} />
            </Box>
          </CardContent>
        </Card>
      </Grid>
    </Grid>
  );
};
