import {
  Add as AddIcon,
  EmailOutlined as Email,
  Phone,
  ArrowForward as ArrowForwardIcon,
} from '@mui/icons-material';
import {
  Box,
  Breadcrumbs,
  Divider,
  Link,
  List,
  ListItem,
  ListItemButton,
  ListItemText,
  ListSubheader,
  Typography,
  IconButton,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  FormControlLabel,
  FormLabel,
  Radio,
  RadioGroup
} from '@mui/material';
import { blueGrey } from '@mui/material/colors';
import { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';
import * as Styled from './Pilots.styled';

interface IPilot {
  id?: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  cellphone: string;
  email: string;
  status: string;
  agreeStatute?: boolean;
  agreeRI?: boolean;
}

export const Pilots = () => {
  const [pilots, setPilots] = useState<Array<IPilot>>([]);
  const [filteredPilots, setFilteredPilots] = useState<Array<IPilot>>([]);
  const [statusType, setStatusType] = useState<string>('statusCadastral');
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [statusOptions, setStatusOptions] = useState<string[]>([]);
  // const { data } = useFetch<Array<IPilot>>({ url: getURI(API.pilots) });

  const navigate = useNavigate();

  const { data: pilotsData } = useFetch<Array<IPilot>>({
    url: getURI(API.pilots)
  });

  // Busca as opções de status cadastral
  const { data: statusCadastralData } = useFetch<string[]>({
    url: getURI(API.pilots) + '/statusCadastral'
  });

  // Busca as opções de status de pagamento
  const { data: statusPaymentData } = useFetch<string[]>({
    url: getURI(API.pilots) + '/statusPayment'
  });

  // Carregar os pilotos
  // useEffect(() => {
  //   if (data && data.length) {
  //     setPilots(data);
  //     setFilteredPilots(data);
  //   }
  // }, [data]);

  // Carregar os pilotos
  useEffect(() => {
    if (pilotsData && pilotsData.length) {
      setPilots(pilotsData);
      setFilteredPilots(pilotsData);
    }
  }, [pilotsData]);

    // Atualizar as opções de status baseado no tipo selecionado
  useEffect(() => {
    if (statusType === 'statusCadastral' && statusCadastralData) {
      setStatusOptions(statusCadastralData);
    } else if (statusType === 'statusPayment' && statusPaymentData) {
      setStatusOptions(statusPaymentData);
    }
  }, [statusType, statusCadastralData, statusPaymentData]);

  // Carregar as opções de status
  useEffect(() => {
    const fetchStatusOptions = async () => {
      try {
        const response = await fetch(getURI(API.pilots) + '/' + statusType);

        if (response.ok) {
          const statusData = await response.json();
          setStatusOptions(statusData);
        } else {
          console.error('Erro ao carregar opções de status');
        }
      } catch (error) {
        console.error('Erro na requisição', error);
      }
    };

    fetchStatusOptions();
  }, [statusType]);

  const handlePilotClick = (pilot: IPilot) => {
    const identifier = pilot.userId;
    navigate(`/pilots/${identifier}`);
  };

  // const handleStatusChange = (event: React.ChangeEvent<{ value: unknown }>) => {
  //   const selectedStatus = event.target.value as string;
  //   setStatusFilter(selectedStatus);
  //   if (selectedStatus) {
  //     const filtered = pilots.filter(
  //       (pilot) => pilot.status === selectedStatus
  //     );
  //     setFilteredPilots(filtered);
  //   } else {
  //     setFilteredPilots(pilots); // Se não houver filtro, exibe todos
  //   }
  // };

  const handleStatusType = (event: React.ChangeEvent<HTMLInputElement>) => {
    const statusType = event.target.value as string;
    setStatusType(statusType);
    setStatusFilter('');
  }

  return (
    <>
      <Box>
        <Box sx={{ padding: '15px' }}>
          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Typography color="text.primary">Pilotos</Typography>
          </Breadcrumbs>
        </Box>

        {/* Filtro de status */}
        <Box
          sx={{
            alignItems: 'center',
            display: 'flex',
            justifyContent: 'space-around',
            padding: '15px 0'
          }}
        >
          <Box sx={{ mr: 2, width: '300px' }}>
            <FormControl>
              <FormLabel id="filterPilotsType">Filtro por status:</FormLabel>
              <RadioGroup
                row
                aria-labelledby="filterPilotsType"
                defaultValue="statusCadastral"
                name="radio-buttons-status"
                onChange={handleStatusType}
              >
                <FormControlLabel value="statusCadastral" control={<Radio />} label="Cadastral" />
                <FormControlLabel value="statusPayment" control={<Radio />} label="Pagamento" />
              </RadioGroup>
            </FormControl>
          </Box>

          <Box sx={{ mr: 2, width: '300px' }}>
            <FormControl fullWidth>
              <InputLabel id="status-filter">
                Status
              </InputLabel>
              <Select
                labelId="status-filter"
                value={statusFilter}
                label="Status"
                onChange={handleStatusChange}
                sx={{ borderRadius: 3 }}
              >
                <MenuItem value="">Todos</MenuItem>
                {statusOptions.map((status) => (
                  <MenuItem key={status} value={status}>
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Box>
        </Box>

        {filteredPilots && (
          <List
            subheader={
              <ListSubheader
                component="div"
                id="nested-list-subheader"
                color="primary"
              >
                Total de {filteredPilots.length} piloto(s) em nossa base de
                dados.
              </ListSubheader>
            }
          >
            {filteredPilots.map((pilot) => (
              <Box key={pilot.userId}>
                <ListItem
                  disablePadding
                  secondaryAction={
                    <IconButton
                      edge="end"
                      aria-label="ver detalhes"
                      onClick={() => handlePilotClick(pilot)}
                    >
                      <ArrowForwardIcon />
                    </IconButton>
                  }
                >
                  <ListItemButton onClick={() => handlePilotClick(pilot)}>
                    <ListItemText
                      primary={`${pilot.firstName} ${pilot.lastName}`}
                      secondary={
                        <span style={{ display: 'flex', alignItems: 'center' }}>
                          <Phone sx={{ fontSize: '14px' }} />
                          <Typography
                            sx={{ paddingLeft: '5px', paddingTop: '2px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.cellphone}
                          </Typography>
                          <Email sx={{ fontSize: '15px', ml: 1 }} />
                          <Typography
                            sx={{ paddingLeft: '5px' }}
                            variant="caption"
                            color={blueGrey[500]}
                          >
                            {pilot.email}
                          </Typography>
                        </span>
                      }
                    />
                  </ListItemButton>
                </ListItem>
                <Divider />
              </Box>
            ))}
          </List>
        )}
      </Box>

      <Styled.AddButton color="primary" aria-label="add">
        <AddIcon />
      </Styled.AddButton>
    </>
  );
};
