import React, { useState, useEffect } from 'react';
import { Link as RouterLink } from 'react-router-dom';
import {
  AppBar,
  Box,
  Button,
  Drawer,
  List,
  ListItem,
  ListItemText,
  IconButton,
  Menu,
  MenuItem
} from '@mui/material';
import {
  // Home,
  // MenuBook,
  // AirplanemodeActive,
  People,
  Menu as MenuIcon,
  Close as CloseIcon,
  ArrowDropDown
} from '@mui/icons-material';

const MenuItems = [
  // {
  //   title: 'Home',
  //   icon: <Home />,
  //   link: '/home'
  // },
  // {
  //   title: 'Sobre',
  //   icon: <MenuBook />,
  //   link: '/about',
  //   submenu: [
  //     {
  //       title: 'Diretoria',
  //       icon: <People />,
  //       link: '/direction'
  //     },
  //     {
  //       title: 'Espaco Aereo',
  //       icon: <AirplanemodeActive />,
  //       link: '/airspace'
  //     },
  //     {
  //       title: 'Estatuto',
  //       icon: <MenuBook />,
  //       link: '/statute'
  //     },
  //     {
  //       title: 'História',
  //       icon: <MenuBook />,
  //       link: '/about'
  //     },
  //     {
  //       title: 'Regimento Interno',
  //       icon: <MenuBook />,
  //       link: '/regiment'
  //     }
  //   ]
  // },
  {
    title: 'Associados',
    icon: <People />,
    link: '/login'
  }
];

export const Navbar: React.FC = () => {
  const [isMenubarOpen, setMenubarOpen] = useState(false);
  const [anchorEl, setAnchorEl] = useState<null | HTMLElement>(null);

  const toggleDrawer = () => {
    setMenubarOpen(!isMenubarOpen);
  };

  const handleMenuClick = (event: React.MouseEvent<HTMLElement>, item: any) => {
    setAnchorEl(event.currentTarget);
  };

  const handleCloseMenu = () => {
    setAnchorEl(null);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth >= 992 && isMenubarOpen) {
        setMenubarOpen(false);
      }
    };
    window.addEventListener('resize', handleResize);
    handleResize();
    return () => window.removeEventListener('resize', handleResize);
  }, [isMenubarOpen]);

  return (
    <AppBar
      position="static"
      sx={{ backgroundColor: 'transparent', boxShadow: 0 }}
    >
      <Box
        sx={{
          display: 'flex',
          justifyContent: 'space-between',
          alignItems: 'center',
          padding: '0 1rem'
        }}
      >

        {/* Menu Desktop */}
        <Box sx={{ display: { sm: 'flex' } }}>
          {MenuItems.map((item) =>
            item (
              <Button
                key={item.title}
                sx={{ color: '#424242', margin: '0 1rem' }}
                component={RouterLink}
                to={item.link}
              >
                <Box sx={{ display: 'flex', alignItems: 'center' }}>
                  {item.icon}
                  <Box sx={{ marginLeft: 0.5 }}>{item.title}</Box>
                </Box>
              </Button>
            )
          )}
        </Box>
      </Box>
    </AppBar>
  )
};
