import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import QRCode from 'qrcode';
import {
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  Typography
} from '@mui/material';

const pix_key_cpvl = process.env.PIX_KEY_CPVL;

interface PaymentProps {
  valor: number;
  totalMissing: number;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ valor, totalMissing }) => {
  const { userId } = useParams<{ userId: string }>();
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  // const [paymentLink, setPaymentLink] = useState<string>('');

  useEffect(() => {
    const paymentUrl = `${pix_key_cpvl}${valor}&descricao=PagamentoExemplo`;

    // Gerando o QR Code quando o valor mudar
    QRCode.toDataURL(paymentUrl)
      .then((qrCode: React.SetStateAction<string>) => {
        setQrCodeImage(qrCode);
        setPaymentLink(paymentUrl);
      })
      .catch((err: undefined) => {
        console.error('Erro ao gerar o QR Code:', err);
      });
  }, [valor]); // Sempre que o valor mudar, o QR Code será atualizado

  const paymentType = (totalMissing: number | undefined): string[] => {
    if (totalMissing === undefined || totalMissing < 0 || totalMissing > 12) {
      return [];
    }

    const types: string[] = [];

    // Monthly is always possible if there are missing months
    if (totalMissing > 0) {
      types.push('mensal');
    }

    // Quarterly possible if missing 3+ months
    if (totalMissing >= 3) {
      types.push('trimestral');
    }

    // Semi-annual possible if missing 6+ months  
    if (totalMissing >= 6) {
      types.push('semestral');
    }

    // Annual only if all 12 months are missing
    if (totalMissing === 12) {
      types.push('anual');
    }

    return types;
  };

  return (
    <div>
      {totalMissing > 0 && (
        <Typography variant="body1" sx={{ mt: 2 }}>
          Faltam {totalMissing} meses.
        </Typography>
      )}
      <FormControl fullWidth>
        <InputLabel id="tipoPagamento">Tipo do pagamento:</InputLabel>
        <Select
          labelId="tipoPagamento"
          id="tipopagamento"
          // value={}
          label="Tipo do pagamento"
        // onChange={handleChange}
        >
          <MenuItem value={paymentType}>paymentType</MenuItem>
          <MenuItem value={'anual'}>Anual</MenuItem>
          <MenuItem value={'mensal'}>Mensal</MenuItem>
          <MenuItem value={'semestral'}>Semestral</MenuItem>
          <MenuItem value={'trimestal'}>Trimestral</MenuItem>
        </Select>
      </FormControl>
      <Typography variant="body1" sx={{ mt: 2 }}>
        Escaneie o QR Code abaixo para efetuar o pagamento.
      </Typography>
      {qrCodeImage && (
        <img src={qrCodeImage} alt="QR Code de Pagamento mensalidade CPVL" />
      )}
      {/* <br />
      <p>Ou copie o link abaixo:</p>
      <input
        type="text"
        value={paymentLink}
        readOnly
        style={{ width: '100%', padding: '8px', marginTop: '8px' }}
      /> */}
    </div>
  );
};

function setPaymentLink(paymentUrl: string) {
  throw new Error('Function not implemented.');
}
