import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { QrCodePix } from 'qrcode-pix';
import {
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  SelectChangeEvent,
  Typography,
  Button,
  Card,
  CardContent,
  Box
} from '@mui/material';

interface PaymentProps {
  valor: number;
  totalMissing: number;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ valor, totalMissing }) => {
  const { userId } = useParams<{ userId: string }>();
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  const [selectedPaymentType, setSelectedPaymentType] = useState<string>('');
  const [pixPayload, setPixPayload] = useState<string>('');
  const [isGeneratingQR, setIsGeneratingQR] = useState<boolean>(false);

  // Função para calcular o valor baseado no tipo de pagamento
  const getPaymentValue = (paymentType: string): number => {
    switch (paymentType) {
      case 'mensal':
        return 50;
      case 'trimestral':
        return 50 * 3; // 150
      case 'semestral':
        return 50 * 6; // 300
      case 'anual':
        return 50 * 12; // 600
      default:
        return 0;
    }
  };

  // Função para obter desconto (se houver)
  const getDiscount = (paymentType: string): number => {
    switch (paymentType) {
      case 'trimestral':
        // return 0.05; // 5% de desconto
        return 0.0;
      case 'semestral':
        // return 0.10; // 10% de desconto
        return 0.0;
      case 'anual':
        // return 0.15; // 15% de desconto
        return 0.0;
      default:
        return 0;
    }
  };

  // Calcular valor final com desconto
  const calculateFinalValue = (paymentType: string): number => {
    const baseValue = getPaymentValue(paymentType);
    const discount = getDiscount(paymentType);
    return baseValue * (1 - discount);
  };

  // eslint-disable-next-line react-hooks/exhaustive-deps
  const generatePixQRCode = async (paymentType: string) => {
    setIsGeneratingQR(true);

    try {
      const finalValue = calculateFinalValue(paymentType);
      console.log('Generating PIX QR Code...', {
        paymentType,
        finalValue,
        userId
      });

      const qrCodePix = QrCodePix({
        version: '01',
        key: process.env.REACT_APP_PIX_KEY_CPVL || '02507679000135',
        name: 'CPVL',
        city: 'POCOS',
        value: finalValue,
        transactionId: `CPVL${userId.substring(0, 10)}_${paymentType}_${Date.now().toString().substring(0, 10)}`,
        // transactionId: `CPVL${userId}_${paymentType}_${Date.now()}`,
        message: `${paymentType} - Usuario ${userId} - ${finalValue.toFixed(
          2
        )}`
      });

      console.log('QrCodePix created:', qrCodePix);

      // Get the payload for copy-paste
      const payload = qrCodePix.payload();
      console.log('Payload generated:', payload);
      setPixPayload(payload);

      // Try different methods to get the QR code image
      try {
        const qrCodeBase64 = await qrCodePix.base64();
        setQrCodeImage(`data:image/png;base64,${qrCodeBase64}`);
        console.log('QR Code generated with base64()');
      } catch (base64Error) {
        console.log(
          'base64() failed, trying alternative method...',
          base64Error
        );

        try {
          const qrCodeDataUrl = await (qrCodePix as any).toImage();
          setQrCodeImage(qrCodeDataUrl);
          console.log('QR Code generated with toImage()');
        } catch (imageError) {
          console.log('toImage() failed, using payload only...', imageError);
          setQrCodeImage('');
        }
      }
    } catch (error) {
      console.error('Erro ao gerar QR Code PIX:', error);
      setQrCodeImage('');
      setPixPayload('');
    } finally {
      setIsGeneratingQR(false);
    }
  };

  useEffect(() => {
    console.log('useEffect triggered:', { selectedPaymentType, userId });

    if (selectedPaymentType) {
      generatePixQRCode(selectedPaymentType);
    } else {
      setQrCodeImage('');
      setPixPayload('');
    }
  }, [generatePixQRCode, selectedPaymentType, userId]);

  const paymentType = (totalMissing: number | undefined): string[] => {
    if (totalMissing === undefined || totalMissing < 0 || totalMissing > 12) {
      return [];
    }

    const types: string[] = [];

    if (totalMissing > 0) types.push('mensal');
    if (totalMissing >= 3) types.push('trimestral');
    if (totalMissing >= 6) types.push('semestral');
    if (totalMissing === 12) types.push('anual');

    return types;
  };

  const handleChange = (event: SelectChangeEvent<string>) => {
    console.log('Payment type selected:', event.target.value);
    setSelectedPaymentType(event.target.value);
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(pixPayload);
      alert('Código PIX copiado para a área de transferência!');
    } catch (err) {
      console.error('Erro ao copiar:', err);
      const textArea = document.createElement('textarea');
      textArea.value = pixPayload;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('Código PIX copiado!');
    }
  };

  const availableTypes = paymentType(totalMissing);
  const currentValue = selectedPaymentType
    ? calculateFinalValue(selectedPaymentType)
    : 0;
  const currentDiscount = selectedPaymentType
    ? getDiscount(selectedPaymentType)
    : 0;
  const baseValue = selectedPaymentType
    ? getPaymentValue(selectedPaymentType)
    : 0;

  return (
    <Box>
      <FormControl fullWidth sx={{ minWidth: 200, mb: 3 }}>
        <InputLabel id="tipoPagamento">Tipo do pagamento</InputLabel>
        <Select
          labelId="tipoPagamento"
          value={selectedPaymentType}
          label="Tipo do pagamento"
          onChange={handleChange}
          disabled={availableTypes.length === 0}
        >
          {availableTypes.length === 0 ? (
            <MenuItem value="" disabled>
              Nenhuma opção disponível
            </MenuItem>
          ) : (
            availableTypes.map((type: string, index: number) => {
              const value = calculateFinalValue(type);
              const discount = getDiscount(type);
              const base = getPaymentValue(type);

              return (
                <MenuItem key={index} value={type}>
                  <div>
                    <div>{type.charAt(0).toUpperCase() + type.slice(1)}</div>
                    <div style={{ fontSize: '12px', color: '#666' }}>
                      {discount > 0 ? (
                        <>
                          <span style={{ textDecoration: 'line-through' }}>
                            R$ {base.toFixed(2)}
                          </span>{' '}
                          <span style={{ color: 'green', fontWeight: 'bold' }}>
                            R$ {value.toFixed(2)} ({(discount * 100).toFixed(0)}
                            % desc.)
                          </span>
                        </>
                      ) : (
                        `R$ ${value.toFixed(2)}`
                      )}
                    </div>
                  </div>
                </MenuItem>
              );
            })
          )}
        </Select>
      </FormControl>

      {selectedPaymentType && (
        <Card sx={{ mt: 2 }}>
          <CardContent sx={{ textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>
              Pagamento PIX -{' '}
              {selectedPaymentType.charAt(0).toUpperCase() +
                selectedPaymentType.slice(1)}
            </Typography>

            {currentDiscount > 0 && (
              <Box sx={{ mb: 1 }}>
                <Typography
                  variant="body2"
                  style={{ textDecoration: 'line-through', color: '#999' }}
                >
                  Valor original: R$ {baseValue.toFixed(2)}
                </Typography>
                <Typography
                  variant="body2"
                  style={{ color: 'green', fontWeight: 'bold' }}
                >
                  Desconto de {(currentDiscount * 100).toFixed(0)}%
                </Typography>
              </Box>
            )}

            <Typography variant="h5" color="primary" sx={{ mb: 2 }}>
              R$ {currentValue.toFixed(2)}
            </Typography>

            {isGeneratingQR && (
              <Typography sx={{ mb: 2 }}>Gerando QR Code PIX...</Typography>
            )}

            {pixPayload && (
              <>
                {qrCodeImage ? (
                  <>
                    <Typography variant="body2" sx={{ mb: 2 }}>
                      Escaneie o QR Code com o app do seu banco:
                    </Typography>

                    <Box sx={{ mb: 2 }}>
                      <img
                        src={qrCodeImage}
                        alt="QR Code PIX"
                        style={{
                          maxWidth: '300px',
                          height: 'auto',
                          border: '1px solid #e0e0e0',
                          borderRadius: '8px'
                        }}
                      />
                    </Box>
                  </>
                ) : (
                  <Typography variant="body2" sx={{ mb: 2, color: 'orange' }}>
                    QR Code image não disponível, use o código PIX abaixo:
                  </Typography>
                )}

                <Typography variant="body2" sx={{ mb: 2 }}>
                  Código PIX (Copia e Cola):
                </Typography>

                <Box sx={{ mb: 2 }}>
                  <textarea
                    value={pixPayload}
                    readOnly
                    rows={4}
                    style={{
                      width: '100%',
                      padding: '8px',
                      fontSize: '11px',
                      fontFamily: 'monospace',
                      border: '1px solid #ddd',
                      borderRadius: '4px',
                      resize: 'none'
                    }}
                  />
                </Box>

                <Button
                  variant="contained"
                  onClick={copyToClipboard}
                  sx={{ mt: 1 }}
                >
                  📋 Copiar Código PIX
                </Button>
              </>
            )}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};

export default PaymentQRCode;
