import React, { useState } from 'react';
import { useParams } from 'react-router-dom';
import QRCodePix, { payload } from 'react-qrcode-pix';
import {
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  SelectChangeEvent,
  Typography,
  Button,
  Card,
  CardContent,
  Box,
  Chip
} from '@mui/material';
import ArrowDownwardIcon from '@mui/icons-material/ArrowDownward';
import { useTheme } from '@mui/material/styles';
import { useFetch } from '../../hooks';
import { API, getURI } from '../../services';

interface PaymentProps {
  totalMissing: number;
}

interface IPaymentMonthly {
  userId: string;
  amount: number;
  ref_year: string;
  ref_month: number;
  type?: string;
  description?: string;
  status?: string;
  date?: Date;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ totalMissing }) => {
  const theme = useTheme();
  const { userId } = useParams<{ userId: string }>();
  const [selectedPaymentType, setSelectedPaymentType] = useState<string>('');
  const [pixCode, setPixCode] = useState<string>('');
  const [notice, setNotice] = useState<string>('');

  const amountMonthly = 50;

  const { doFetch } = useFetch<IPaymentMonthly>({ method: 'POST' });

  const handlePaymentNotice = async () => {
    if (!userId) return;

    const currentValue = calculateFinalValue(selectedPaymentType);
    console.log('Valor a ser pago:', currentValue);


    try {
      await doFetch({
        url: `${API.createPaymentMonthly}`,
        body: {
          userId,
          amount: currentValue, // valor calculado
          ref_year: new Date().getFullYear(),
          ref_month: new Date().getMonth() + 1,
          type: selectedPaymentType,
          description: `Pagamento ${selectedPaymentType}`,
          status: 'Confirmar',
          date: new Date()
        },
        method: 'POST'
      });

      setSelectedPaymentType('');
      setNotice('Avisado! Lançaremos em breve!');
    } catch (err) {
      console.error('Erro ao avisar pagamento', err);
    }
  };

  const getPaymentValue = (paymentType: string): number => {
    switch (paymentType) {
      case 'mensal':
        return amountMonthly;
      case 'trimestral':
        return amountMonthly * 3;
      case 'semestral':
        return amountMonthly * 6;
      case 'anual':
        return amountMonthly * 12;
      default:
        return 0;
    }
  };

  const getDiscount = (paymentType: string): number => {
    switch (paymentType) {
      case 'trimestral':
        return 0.0; // 0.05;
      case 'semestral':
        return 0.0; // 0.10;
      case 'anual':
        return 0.0; // 0.15;
      default:
        return 0;
    }
  };

  const calculateFinalValue = (paymentType: string): number => {
    const baseValue = getPaymentValue(paymentType);
    const discount = getDiscount(paymentType);
    return Math.round(baseValue * (1 - discount) * 100) / 100;
  };

  const paymentType = (totalMissing: number | undefined): string[] => {
    if (totalMissing === undefined || totalMissing < 0 || totalMissing > 12) {
      return [];
    }

    const types: string[] = [];
    if (totalMissing > 0) types.push('mensal');
    if (totalMissing >= 3) types.push('trimestral');
    if (totalMissing >= 6) types.push('semestral');
    if (totalMissing === 12) types.push('anual');

    return types;
  };

  const handleChange = (event: SelectChangeEvent<string>) => {
    setSelectedPaymentType(event.target.value);
    setPixCode(''); // Clear previous code
  };

  const copyToClipboard = async () => {
    if (pixCode) {
      try {
        await navigator.clipboard.writeText(pixCode);
        alert('Código PIX copiado para a área de transferência!');
      } catch (err) {
        const textArea = document.createElement('textarea');
        textArea.value = pixCode;
        document.body.appendChild(textArea);
        textArea.select();
        document.execCommand('copy');
        document.body.removeChild(textArea);
        alert('Código PIX copiado!');
      }
    }
  };

  const availableTypes = paymentType(totalMissing);
  const currentValue = selectedPaymentType
    ? calculateFinalValue(selectedPaymentType)
    : 0;
  const currentDiscount = selectedPaymentType
    ? getDiscount(selectedPaymentType)
    : 0;
  const baseValue = selectedPaymentType
    ? getPaymentValue(selectedPaymentType)
    : 0;

  return (
    <Card
      sx={{
        width: '100%',
        padding: 2
      }}
    >
      <CardContent>
        {/* Aviso ou FormControl */}
        {notice ? (
          <Typography
            variant="h6"
            color="white"
            sx={{
              bgcolor: 'warning.main',
              borderRadius: '6px',
              padding: 1,
              textAlign: 'center',
              mb: 3
            }}
          >
            {notice}
          </Typography>
        ) : totalMissing > 0 ? (
          <Box sx={{ mb: 3 }}>
            <Typography variant="body1" color="primary" sx={{ mb: 2 }}>
              {totalMissing} meses à pagar.
            </Typography>

            <FormControl fullWidth sx={{ minWidth: 280, mb: 1 }}>
              <InputLabel id="tipoPagamento">Tipo do pagamento</InputLabel>
              <Select
                labelId="tipoPagamento"
                value={selectedPaymentType}
                label="Tipo do pagamento"
                onChange={handleChange}
                disabled={availableTypes.length === 0}
              >
                {availableTypes.map((type: string, index: number) => {
                  const amount = calculateFinalValue(type);
                  const discount = getDiscount(type);

                  return (
                    <MenuItem key={index} value={type}>
                      <Box
                        sx={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          width: '100%'
                        }}
                      >
                        <span>
                          {type.charAt(0).toUpperCase() + type.slice(1)}
                        </span>
                        <Box sx={{ textAlign: 'right' }}>
                          {discount > 0 && (
                            <Chip
                              label={`-${(discount * 100).toFixed(0)}%`}
                              size="small"
                              color="success"
                              sx={{ mr: 1 }}
                            />
                          )}
                          <span style={{ fontWeight: 'bold' }}>
                            R$ {amount.toFixed(2)}
                          </span>
                        </Box>
                      </Box>
                    </MenuItem>
                  );
                })}
              </Select>
            </FormControl>
          </Box>
        ) : (
          <Typography variant="body1" color="primary" sx={{ mb: 3 }}>
            {totalMissing} meses à pagar.
            <br />
            CPVL agradece!
          </Typography>
        )}

        {/* Grid com 3 Colunas Responsivas */}
        {selectedPaymentType && (
          <Grid container spacing={3} alignItems="flex-start">
            {/* Coluna 1: Informações do Pagamento */}
            <Grid item xs={12} md={4}>
              <Box>
                <Typography variant="h6" color="primary" gutterBottom>
                  Pagamento PIX
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  {selectedPaymentType.charAt(0).toUpperCase() +
                    selectedPaymentType.slice(1)}
                </Typography>

                {currentDiscount > 0 && (
                  <Chip
                    label={`Economize ${(currentDiscount * 100).toFixed(
                      0
                    )}% - R$ ${(baseValue - currentValue).toFixed(2)}`}
                    color="success"
                    sx={{ mb: 2 }}
                  />
                )}

                <Typography variant="h5" sx={{ fontWeight: 'bold' }}>
                  R$ {currentValue.toFixed(2)}
                </Typography>
              </Box>
            </Grid>

            {/* Coluna 2: QR Code */}
            <Grid item xs={12} md={4}>
              <Box sx={{ textAlign: 'center' }}>
                <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
                  <QRCodePix
                    pixkey={process.env.REACT_APP_PIX_KEY_CPVL || ''}
                    merchant="CPVL"
                    city="POCOS CALDAS"
                    cep="37701000"
                    amount={currentValue}
                    size={200}
                    bgColor="#FFFFFF"
                    level="M"
                    onLoad={(payload) => setPixCode(payload)}
                  />
                </Box>

                {pixCode && (
                  <Box>
                    <Typography variant="body2" sx={{ mb: 1 }}>
                      Ou
                    </Typography>
                    <Button
                      variant="contained"
                      size="medium"
                      onClick={copyToClipboard}
                      sx={{
                        borderRadius: 2,
                        textTransform: 'none'
                      }}
                    >
                      Código PIX (Copia/Cola)
                    </Button>
                  </Box>
                )}
              </Box>
            </Grid>

            {/* Coluna 3: Confirmação */}
            <Grid item xs={12} md={4}>
              <Box
                sx={{
                  display: 'flex',
                  flexDirection: 'column',
                  alignItems: 'center'
                }}
              >
                <Typography variant="h6" color="primary" gutterBottom>
                  Após o Pagamento
                </Typography>
                <ArrowDownwardIcon
                  color="primary"
                  fontSize="large"
                  sx={{ mb: 2 }}
                />
                <Button
                  color="warning"
                  variant="contained"
                  size="large"
                  onClick={handlePaymentNotice}
                  sx={{
                    borderRadius: 2,
                    textTransform: 'none',
                    padding: '12px 24px'
                  }}
                >
                  Avisar o pagamento
                </Button>
              </Box>
            </Grid>
          </Grid>
        )}
      </CardContent>
    </Card>
  );
};
