import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { QrCodePix } from 'qrcode-pix';
import {
  FormControl,
  InputLabel,
  MenuItem,
  Select,
  SelectChangeEvent,
  Typography,
  Button,
  Card,
  CardContent,
  Box
} from '@mui/material';

interface PaymentProps {
  valor: number;
  totalMissing: number;
}

export const PaymentQRCode: React.FC<PaymentProps> = ({ valor, totalMissing }) => {
  const { userId } = useParams<{ userId: string }>();
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  const [selectedPaymentType, setSelectedPaymentType] = useState<string>('');
  const [pixPayload, setPixPayload] = useState<string>('');
  const [isGeneratingQR, setIsGeneratingQR] = useState<boolean>(false);

  interface PaymentType {
    value: string;
    label: string;
  }

  const paymentTypes: PaymentType[] = [
    { value: 'mensal', label: 'R$ 50,00' },
    { value: 'trimestral', label: 'R$ 150,00' },
    { value: 'semestral', label: 'R$ 300,00' },
    { value: 'anual', label: 'R$ 600,00' }
  ];

  const generatePixQRCode = async (paymentType: string, amount: number) => {
    setIsGeneratingQR(true);

    try {
      const qrCodePix = QrCodePix({
        version: '01',
        key: process.env.REACT_APP_PIX_KEY_CPVL || '',
        name: 'CLUBE POOS CALDENSE DE VO LIVRE',
        city: 'POOS DE CALDAS',
        value: amount,
        transactionId: `CPVL${userId}_${paymentType}_${Date.now()}`,
        message: `Mensalidade ${paymentType} - Usuario ${userId}`,
        cep: '37701-100',
        currency: 986,
        countryCode: 'BR'
      });

      // Get the payload for copy-paste
      const payload = qrCodePix.payload();
      setPixPayload(payload);

      // Generate QR code image directly using qrcode_pix
      const qrCodeDataUrl = qrCodePix.base64(); // This generates the base64 image
      setQrCodeImage(`data:image/png;base64,${qrCodeDataUrl}`);

    } catch (error) {
      console.error('Erro ao gerar QR Code PIX:', error);
    } finally {
      setIsGeneratingQR(false);
    }
  };

  useEffect(() => {
    if (selectedPaymentType && valor > 0) {
      generatePixQRCode(selectedPaymentType, valor);
    } else {
      setQrCodeImage('');
      setPixPayload('');
    }
  }, [selectedPaymentType, valor, userId]);

  const paymentType = (totalMissing: number | undefined): string[] => {
    if (totalMissing === undefined || totalMissing < 0 || totalMissing > 12) {
      return [];
    }

    const types: string[] = [];

    if (totalMissing > 0) types.push('mensal');
    if (totalMissing >= 3) types.push('trimestral');
    if (totalMissing >= 6) types.push('semestral');
    if (totalMissing === 12) types.push('anual');

    return types;
  };

  const handleChange = (event: SelectChangeEvent<string>) => {
    setSelectedPaymentType(event.target.value);
  };

  const copyToClipboard = async () => {
    try {
      await navigator.clipboard.writeText(pixPayload);
      alert('C贸digo PIX copiado para a 谩rea de transfer锚ncia!');
    } catch (err) {
      console.error('Erro ao copiar:', err);
      // Fallback para navegadores mais antigos
      const textArea = document.createElement('textarea');
      textArea.value = pixPayload;
      document.body.appendChild(textArea);
      textArea.select();
      document.execCommand('copy');
      document.body.removeChild(textArea);
      alert('C贸digo PIX copiado!');
    }
  };

  const availableTypes = paymentType(totalMissing);

  return (
    <Box>      
      <FormControl sx={{ display: 'flex', justifyContent: 'center', minWidth: 200, mt: 2 }}>
        <InputLabel id="tipoPagamento">Tipo do pagamento</InputLabel>
        <Select
          labelId="tipoPagamento"
          value={selectedPaymentType}
          label="Tipo do pagamento"
          onChange={handleChange}
          disabled={availableTypes.length === 0}
        >
          {availableTypes.length === 0 ? (
            <MenuItem value="" disabled>
              Nenhuma op莽茫o dispon铆vel
            </MenuItem>
          ) : (
            availableTypes.map((type: string, index: number) => (
              <MenuItem key={index} value={type}>
                {type.charAt(0).toUpperCase() + type.slice(1)}
              </MenuItem>
            ))
          )}
        </Select>
      </FormControl>

      {selectedPaymentType && (
        <Card sx={{ mt: 2 }}>
          <CardContent sx={{ textAlign: 'center' }}>
            <Typography variant="h6" gutterBottom>
              Pagamento PIX - {selectedPaymentType.charAt(0).toUpperCase() + selectedPaymentType.slice(1)}
            </Typography>

            <Typography variant="h5" color="primary" sx={{ mb: 2 }}>
              R$ {valor.toFixed(2)}
            </Typography>

            {isGeneratingQR ? (
              <Typography>Gerando QR Code PIX...</Typography>
            ) : qrCodeImage ? (
              <>
                <Typography variant="body2" sx={{ mb: 2 }}>
                  Escaneie o QR Code com o app do seu banco:
                </Typography>

                <Box sx={{ mb: 2 }}>
                  <img 
                    src={qrCodeImage} 
                    alt="QR Code PIX"
                    style={{ 
                      maxWidth: '300px', 
                      height: 'auto',
                      border: '1px solid #e0e0e0',
                      borderRadius: '8px'
                    }}
                  />
                </Box>

                <Typography variant="body2" sx={{ mb: 2 }}>
                  Ou copie o c贸digo PIX:
                </Typography>

                <Box sx={{ mb: 2 }}>
                  <textarea
                    value={pixPayload}
                    readOnly
                    rows={3}
                    style={{
                      width: '100%',
                      padding: '8px',
                      fontSize: '12px',
                      fontFamily: 'monospace',
                      border: '1px solid #ddd',
                      borderRadius: '4px',
                      resize: 'none'
                    }}
                  />
                </Box>

                <Button 
                  variant="contained" 
                  onClick={copyToClipboard}
                  sx={{ mt: 1 }}
                >
                   Copiar C贸digo PIX
                </Button>
              </>
            ) : null}
          </CardContent>
        </Card>
      )}
    </Box>
  );
};
