    //   }

    //   // Recarregar a lista de pilotos do servidor (garante consistência)
    //   try {
    //     await fetchPilots({ url: getURI(API.pilots), method: 'GET' });
    //   } catch (e) {
    //     // se falhar apenas atualiza localmente (optimistic)
    //     setPilots((prev) =>
    //       prev.filter((p) => {
    //         if (p.userId && p.userId === idToDelete) return false;
    //         if (p.id && p.id === idToDelete) return false;
    //         return true;
    //       })
    //     );
    //     setFilteredPilots((prev) =>
    //       prev.filter((p) => {
    //         if (p.userId && p.userId === idToDelete) return false;
    //         if (p.id && p.id === idToDelete) return false;
    //         return true;
    //       })
    //     );
    //   }

    //   setOpenConfirm(false);
    //   setPilotToDelete(null);
    // } catch (err: any) {
    //   const msg = err && err.message ? err.message : 'Erro ao remover usuário';
    //   setActionError(msg);
    // }
  };

  return (
    <>
      <Box>
        <Box sx={{ padding: '15px' }}>
          <Breadcrumbs aria-label="breadcrumb">
            <Link underline="hover" color="inherit" href="/">
              Dashboard
            </Link>
            <Typography color="text.primary">Pilotos</Typography>
          </Breadcrumbs>
        </Box>

        {/* Filtro de status */}
        <Box
          sx={{
            alignItems: 'center',
            display: 'flex',
            justifyContent: 'space-around',
            padding: '15px 0'
          }}
        >
          <Box sx={{ mr: 2, width: '300px' }}>
            <FormControl>
              <FormLabel id="filterPilotsType">Filtro por status:</FormLabel>
              <RadioGroup
                row
                aria-labelledby="filterPilotsType"
                defaultValue="statusPayment"
                name="radio-buttons-status"
                onChange={handleStatusType}
              >
                <FormControlLabel
                  value="statusCadastral"
                  control={<Radio />}
                  label="Cadastral"
                />
                <FormControlLabel
                  value="statusPayment"
                  control={<Radio />}
                  label="Pagamento"
                />
              </RadioGroup>
            </FormControl>
          </Box>

          <Box sx={{ mr: 2, width: '300px' }}>
            <FormControl fullWidth>
              <InputLabel id="status-filter">Status</InputLabel>
              <Select
                labelId="status-filter"
                value={statusFilter}
                label="Status"
                onChange={handleStatusChange}
                sx={{ borderRadius: 3 }}
              >
                <MenuItem value="">Todos</MenuItem>
                {statusOptions &&
                  statusOptions.length > 0 &&
                  statusOptions.map((status) => (
                    <MenuItem key={status} value={status}>
                      {status.charAt(0).toUpperCase() + status.slice(1)}
                    </MenuItem>
                  ))}
              </Select>
            </FormControl>
          </Box>
        </Box>

        {/* mensagens */}
        {actionMessage && (
          <Box sx={{ px: 2, mb: 1 }}>
            <Typography variant="body2" color="success.main">
              {actionMessage}
            </Typography>
          </Box>
        )}
        {actionError && (
          <Box sx={{ px: 2, mb: 1 }}>
            <Typography variant="body2" color="error.main">
              {actionError}
            </Typography>
          </Box>
        )}

        <Box>
          {loadingPilots ? (
            <Box display="flex" justifyContent="center" py={6}>
              <CircularProgress />
            </Box>
          ) : (
            <List
              subheader={
                <ListSubheader component="div" color="primary">
                  Total de {filteredPilots ? filteredPilots.length : 0}{' '}
                  piloto(s) em nossa base de dados.
                </ListSubheader>
              }
            >
              {filteredPilots &&
                filteredPilots.map((pilot) => (
                  <Box key={pilot.userId}>
                    <ListItem
                      disablePadding
                      secondaryAction={
                        <Box sx={{ display: 'flex', alignItems: 'center' }}>
                          {/* {auth && auth.user && auth.user.role === 'admin' && ( */}
                          <IconButton
                            edge="end"
                            aria-label="deletar"
                            onClick={() => openDeleteDialog(pilot)}
                            sx={{ color: 'error.main', mr: 1 }}
                            disabled={deleting}
                            title="Excluir usuário/piloto"
                          >
                            <DeleteIcon />
                          </IconButton>
                          {/* )} */}
                          <IconButton
                            edge="end"
                            aria-label="ver detalhes"
                            onClick={() => handlePilotClick(pilot)}
                          >
                            <ArrowForwardIcon />
                          </IconButton>
                        </Box>
                      }
                    >
                      <ListItemButton onClick={() => handlePilotClick(pilot)}>
                        <ListItemText
                          primary={
                            pilot.firstName && pilot.firstName !== ''
                              ? pilot.firstName + ' ' + (pilot.lastName || '')
                              : pilot.email || '---'
                          }
                          secondary={
                            <span
                              style={{ display: 'flex', alignItems: 'center' }}
                            >
                              <Phone sx={{ fontSize: '14px' }} />
                              <Typography
                                sx={{ paddingLeft: '5px', paddingTop: '2px' }}
                                variant="caption"
                                color={blueGrey[500]}
                              >
                                {pilot.cellphone}
                              </Typography>
                              <Email sx={{ fontSize: '15px', ml: 1 }} />
                              <Typography
                                sx={{ paddingLeft: '5px' }}
                                variant="caption"
                                color={blueGrey[500]}
                              >
                                {pilot.email}
                              </Typography>
                            </span>
                          }
                        />
                      </ListItemButton>
                    </ListItem>
                    <Divider />
                  </Box>
                ))}
            </List>
          )}
        </Box>
      </Box>

      {/* Dialog de confirmação */}
      <Dialog open={openConfirm} onClose={closeDeleteDialog}>
        <DialogTitle>Confirmar exclusão</DialogTitle>
        <DialogContent>
          <DialogContentText>
            Deseja realmente excluir o usuário/piloto{' '}
            <strong>
              {pilotToDelete
                ? pilotToDelete.firstName && pilotToDelete.firstName !== ''
                  ? pilotToDelete.firstName +
                    ' ' +
                    (pilotToDelete.lastName || '')
                  : pilotToDelete.email || ''
                : ''}
            </strong>
            {pilotToDelete
              ? ' (id: ' +
                (pilotToDelete.userId
                  ? pilotToDelete.userId
                  : pilotToDelete.id
                  ? pilotToDelete.id
                  : '?') +
                ')'
              : ''}
            ? Esta ação removerá também os dados associados.
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={closeDeleteDialog} disabled={deleting}>
            Cancelar
          </Button>
          <Button
            onClick={confirmDelete}
            color="error"
            variant="contained"
            disabled={deleting}
          >
            {deleting ? 'Excluindo...' : 'Excluir'}
          </Button>
        </DialogActions>
      </Dialog>

      <Styled.AddButton color="primary" aria-label="add">
        <AddIcon />
      </Styled.AddButton>
    </>
  );
};
