import React, { useState, useEffect } from 'react';
import {
  Box,
  Paper,
  Typography,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
  Chip,
  Card,
  CardContent,
  Grid,
  TextField,
  InputAdornment,
  Tab,
  Tabs,
  CircularProgress,
  Alert,
  Avatar,
  Tooltip,
  IconButton
} from '@mui/material';
import {
  Search as SearchIcon,
  CheckCircle as CheckCircleIcon,
  Cancel as CancelIcon,
  Person as PersonIcon,
  Email as EmailIcon,
  Phone as PhoneIcon,
  Refresh as RefreshIcon
} from '@mui/icons-material';
import { green, red, orange } from '@mui/material/colors';

interface PilotStatusData {
  paymentStatus: ReactNode;
  paymentValue: ReactNode;
  id: number;
  userId: number;
  firstName: string;
  lastName: string;
  cpf: string;
  email: string;
  cellphone: string;
  status: string;
  isAuthorized: boolean;
  lastPaymentMonth: number | null;
  lastPaymentYear: number | null;
  lastPaymentAmount: number | null;
  lastPaymentDate: string | null;
  monthsUpToDate: number;
}

interface StatusListResponse {
  success: boolean;
  summary?: {
    total: number;
    authorized: number;
    unauthorized: number;
    authorizationRate: string;
  };
  data?: {
    authorized: PilotStatusData[];
    unauthorized: PilotStatusData[];
    all: PilotStatusData[];
  };
  count?: number;
  currentMonth: number;
  currentYear: number;
}

export const StatusList = () => {
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [data, setData] = useState<StatusListResponse | null>(null);
  const [searchTerm, setSearchTerm] = useState<string>('');
  const [currentTab, setCurrentTab] = useState<number>(0);

  const monthNames = [
    'Jan',
    'Fev',
    'Mar',
    'Abr',
    'Mai',
    'Jun',
    'Jul',
    'Ago',
    'Set',
    'Out',
    'Nov',
    'Dez'
  ];

  const fetchStatusList = async () => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/status-list/all', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem('token')}`,
          'Content-Type': 'application/json'
        }
      });

      if (!response.ok) {
        throw new Error('Erro ao carregar dados');
      }

      const result: StatusListResponse = await response.json();
      setData(result);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Erro desconhecido');
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStatusList();
  }, []);

  const handleTabChange = (_: React.SyntheticEvent, newValue: number) => {
    setCurrentTab(newValue);
  };

  const filterPilots = (pilots: PilotStatusData[]) => {
    return pilots.filter(
      (pilot) =>
        pilot.firstName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        pilot.lastName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        pilot.cpf.includes(searchTerm) ||
        pilot.email.toLowerCase().includes(searchTerm.toLowerCase())
    );
  };


  const getStatusChip = (pilot: PilotStatusData) => {
    if (pilot.isAuthorized) {
      return (
        <Chip
          icon={<CheckCircleIcon />}
          label="Autorizado"
          color="success"
          variant="filled"
          size="small"
        />
      );
    } else {
      return (
        <Chip
          icon={<CancelIcon />}
          label="NÃ£o Autorizado"
          color="error"
          variant="filled"
          size="small"
        />
      );
    }
  };

  // const getPaymentStatus = (pilot: PilotStatusData) => {
  //   const currentMonth = data?.currentMonth || 9;
  //   const monthsUpToDate = pilot.monthsUpToDate;

  //   if (monthsUpToDate === currentMonth) {
  //     return { color: green[500], text: 'Em dia' };
  //   } else if (monthsUpToDate >= currentMonth - 2) {
  //     return {
  //       color: orange[500],
  //       text: `${currentMonth - monthsUpToDate} mÃªs(es) em atraso`
  //     };
  //   } else {
  //     return {
  //       color: red[500],
  //       text: `${currentMonth - monthsUpToDate} meses em atraso`
  //     };
  //   }
  // };

  // Componente StatCard
  const StatCard: React.FC<{
    title: string;
    value: number;
    color: string;
    icon: React.ReactNode;
  }> = ({ title, value, color, icon }) => (
    <Card sx={{ height: '100%' }}>
      <CardContent>
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 2 }}>
          <Avatar sx={{ bgcolor: color, mr: 2 }}>{icon}</Avatar>
          <Typography variant="h6" component="div">
            {title}
          </Typography>
        </Box>
        <Typography variant="h4" component="div" sx={{ color: color }}>
          {value}
        </Typography>
      </CardContent>
    </Card>
  );

  // Componente PilotTable
  const PilotTable: React.FC<{ pilots: PilotStatusData[] }> = ({ pilots }) => {
    const filteredPilots = filterPilots(pilots);

    return (
      <TableContainer component={Paper} sx={{ mt: 2 }}>
        <Table>
          <TableHead>
            <TableRow sx={{ bgcolor: 'grey.50' }}>
              <TableCell>
                <strong>Nome</strong>
              </TableCell>
              <TableCell>
                <strong>CPF</strong>
              </TableCell>
              <TableCell>
                <strong>Status</strong>
              </TableCell>
              <TableCell>
                <strong>SituaÃ§Ã£o Pagamento</strong>
              </TableCell>
              <TableCell>
                <strong>Ãltimo Pagamento</strong>
              </TableCell>
              <TableCell>
                <strong>Valor</strong>
              </TableCell>
              <TableCell>
                <strong>Contato</strong>
              </TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {filteredPilots.map((pilot) => (
              <TableRow key={pilot.id}>
                <TableCell>{`${pilot.firstName} ${pilot.lastName}`}</TableCell>
                <TableCell>{pilot.cpf}</TableCell>
                <TableCell>{getStatusChip(pilot)}</TableCell>
                <TableCell>{pilot.paymentStatus}</TableCell>
                <TableCell>{pilot.lastPaymentDate}</TableCell>
                <TableCell>{pilot.paymentValue}</TableCell>
                <TableCell>{pilot.email}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    );
  };
};
