import React, { useState } from 'react';
import { TextField, Button, Box, Typography, IconButton, Snackbar } from '@mui/material';
import ContentCopyIcon from '@mui/icons-material/ContentCopy';
import QRCode from 'qrcode.react';
import { QrCodePix } from 'qrcode-pix';
import { useParams } from 'react-router-dom';


interface PaymentProps {
  valor: number;
}

const PaymentQRCode: React.FC<PaymentProps> = ({ valor }) => {
  const valorFormatado = valor.toFixed(2);  
  const [qrCodeImage, setQrCodeImage] = useState<string>('');
  const [paymentLink, setPaymentLink] = useState<string>('');

  useEffect(() => {
    const paymentUrl = `https://link-de-pagamento-do-itau.com?valor=${valor}&descricao=PagamentoExemplo`;

    // Gerando o QR Code quando o valor mudar
    QRCode.toDataURL(paymentUrl)
      .then((qrCode) => {
        setQrCodeImage(qrCode);
        setPaymentLink(paymentUrl);
      })
      .catch((err) => {
        console.error('Erro ao gerar o QR Code:', err);
      });
  }, [valor]); // Sempre que o valor mudar, o QR Code será atualizado

  return (
    <div>
      <h1>Pagamento</h1>
      <p>Escaneie o QR Code abaixo para efetuar o pagamento.</p>
      {qrCodeImage && <img src={qrCodeImage} alt="QR Code de Pagamento" />}
      <br />
      <p>Ou copie o link abaixo:</p>
      <input
        type="text"
        value={paymentLink}
        readOnly
        style={{ width: '100%', padding: '8px', marginTop: '8px' }}
      />
    </div>
  );
};

export default PaymentQRCode;
